// The settings you can change are:
// - whether to erode or dilate
// - what channel to use as the value for a pixel
// - what distance metric to use (p-norm)
// - the radius to search within

// You can experiment with your own channel values or distance metrics by
// changing the channelValue or distanceFunction functions :)

// If you change the iChannel0 texture here, make sure to change it in the Image
// buffer as well

#define EROSION   0
#define DILATION  1

#define RED       0
#define GREEN     1
#define BLUE      2
#define ALPHA     3
#define LUMINANCE 4

// Settings (have to reset time/change resolution to see effects):
#define MODE    DILATION
#define CHANNEL LUMINANCE
#define P_NORM  1.0
#define RADIUS  10.0

float channelValue(vec4 color) {
    switch (CHANNEL) {
        case RED:       return color.r;
        case GREEN:     return color.g;
        case BLUE:      return color.b;
        case ALPHA:     return color.a;
        case LUMINANCE: return dot(color.rgb, vec3(0.2126, 0.7152, 0.0722));
    }
}

float distanceFunction(vec2 a, vec2 b) {
    return pow(pow(abs(b.x - a.x), P_NORM) + pow(abs(b.y - a.y), P_NORM), 1.0 / P_NORM);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec4 d = texelFetch(iChannel1, ivec2(0), 0);

    if (iFrame == 0 || d.a != iResolution.x) {
        fragColor.b = 0.0;
    } else {
        fragColor.b = d.b + 1.0;
    }

    fragColor.a = iResolution.x;
    int iFrame = int(fragColor.b);
    ivec2 p = ivec2(fragCoord);
    fragColor.rg = vec2(0.0);

    if (iFrame == 0) {        
        fragColor.rg = fragCoord;        
        return;
    }

    int numSteps = int(ceil(log2(max(iResolution.x, iResolution.y))));

    if (iFrame > numSteps) {        
        fragColor = texelFetch(iChannel1, p, 0);
        return;
    }

    int k = 1 << numSteps - iFrame;    
    float best = MODE == EROSION ? 1e30 : -1e30;

    for (int i = -k; i <= k; i += k) {
        for (int j = -k; j <= k; j += k) {
            ivec2 s = p + ivec2(i, j);

            if (any(lessThan(ivec4(s, iResolution.xy - 1.0), ivec4(0, 0, s)))) {
                continue;
            }

            vec2 q = texelFetch(iChannel1, s, 0).xy;

            if (q == vec2(0.0)) {
                continue;
            }

            float d = distanceFunction(fragCoord, q);
            float v = channelValue(texelFetch(iChannel0, ivec2(q) % textureSize(iChannel0, 0), 0));

            if (d < RADIUS && (MODE == EROSION ? v < best : v > best)) {
                best = v;
                fragColor.rg = q;
            }
        }
    }
}

