// ------------------
// Epic Dither Shader
// ------------------
// by Mors
// CC BY 4.0
//
// Originally written for a secret project, later modified to work in ShaderToy.
// Should be easy to port to game engines such as GameMaker.
// Shoutouts to Catonator for providing me with the pattern, and just overall support. :)


// Resolution of the base image
const vec2 RESOLUTION = vec2(320.0, 180.0);

// Number of colors in each channel
const float COLORS_PER_CHANNEL = 8.0;

// Strength of the dithering effect, from 0.0 to 1.0
const float DITHER_STRENGTH = 1.0;

// Size of the dither texture
const float BAYER_SIZE = 8.0;

// 8x8 bayer ordered dithering pattern. Each input pixel
// is scaled to the 0..63 range before looking in this table
// to determine the action
const float BAYER_TEXTURE[8 * 8] = float[](
     0., 32.,  8., 40.,  2., 34., 10., 42.,
    48., 16., 56., 24., 50., 18., 58., 26.,
    12., 44.,  4., 36., 14., 46.,  6., 38.,
    60., 28., 52., 20., 62., 30., 54., 22.,
     3., 35., 11., 43.,  1., 33.,  9., 41.,
    51., 19., 59., 27., 49., 17., 57., 25.,
    15., 47.,  7., 39., 13., 45.,  5., 37.,
    63., 31., 55., 23., 61., 29., 53., 21.
    );


// Getting the specific pattern from the grid
float getBayer(vec2 uvScreenSpace)
{
    vec2 uv = mod(uvScreenSpace.xy / (iResolution.xy / RESOLUTION), BAYER_SIZE);
    return BAYER_TEXTURE[int(uv.y) * int(BAYER_SIZE) + int(uv.x)] / (BAYER_SIZE * BAYER_SIZE);
}

// Crushing the colors
float quantize(float channel, float period)
{
    return floor((channel + period / 2.0) / period) * period;
}

// Where the magic happens
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 period = vec3(1.0 / (COLORS_PER_CHANNEL - 1.0));

    vec3 col = texture(iChannel0, fragCoord / iResolution.xy).rgb;
    col += (getBayer(fragCoord) - 0.5) * period * DITHER_STRENGTH;
    col = vec3(quantize(col.r, period.r),
               quantize(col.g, period.g),
               quantize(col.b, period.b));

    fragColor = vec4(col, 1.0);
}

