// Adaptive variant of https://shadertoy.com/view/Dsj3zD
// Refactored fork of "Koch Snowflake Optimized" by mla. https://shadertoy.com/view/csX3Rs
// ( Uses an explicit stack to keep track of the recursion + short-cuts to draw only what's necessary )
// I replace program symbols by true segments lengthes and angles.

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0., 1.);
  return length(p-b*h);
}

vec2 p = vec2(0);     // The drawing point

float drawsegment(vec2 U, float seglen, float a) {
  vec2 seg = seglen*vec2(cos(a),sin(a)),
         q = p + seg;
  float  d = segment(U,p,q);
  p = q;
  return d;
}

void mainImage(out vec4 O, vec2 u) {
    float END = -1., factor = 3.,    // scaling factor betweel levels
#if 0                   // von Koch snowflakes: F  F+F--F+F
    program[] = float[] (0.,1., 1.05,1., -2.1,1., 1.05,1., 0.,END);// (angle, length)
#elif 0                 // Kochawave
    program[] = float[] (0.,1., .524,1.732, -2.62,1., 2.1,1., 0.,END);
#else                   // interpolation
    X = 1.-.5*cos(iTime), S = .866,             // von Koch to Kochawave
 // X = .5 - cos(iTime) , S = .866,             // left to right Kochawave
    program[] = float[] ( 0., 1.,               // (angle, length)
                          atan(S,X), sqrt(S*S+X*X), 
                          atan(X,S)-atan(X-1.,S)-3.14, sqrt(S*S+(X-1.)*(X-1.)), 
                          atan(X-1.,S)+1.57,1., 
                          0., END );
#endif 
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y + vec2(1.5,.8);
    const int maxdepth = 10;
    int stack[maxdepth+1], depth = -1;     // stack: pos in program + seg length
    float lens[6];
    stack[++depth] = 0; lens[depth] = 1.;
    float d = 1e8, seglen = 1., theta = 0.;     // Cumulative angle

    while (depth >= 0) {
        float len = program[2*stack[depth] +1]; // len
        theta +=    program[2*stack[depth]++];  // turns
        if (len == END)
            seglen = lens[depth--];             // Terminate level
        else   
        if (depth == maxdepth || seglen < 3./R.y )  
              d = min(d,drawsegment(U,seglen*len,theta)); // Draw segment
        else {
          vec2 seg = seglen*len*vec2(cos(theta),sin(theta));
          if (distance(U,p+.5*seg) > .5*length(seg))
              p += seg;                         // skip unseen segments
          else
              stack[++depth]=0, lens[depth]=seglen, seglen *= len/factor; // Recurse
        }
    }
    O =  vec4(smoothstep(0.,2./R.y,d));
}

// variant of https://shadertoy.com/view/ds23zW

#define cross(a,b)  ( (a).x*(b).y - (a).y*(b).x )
#define rot(a)      mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 O, vec2 u )
{
    float s = sqrt(3.)/2., i=0., a,b,
          T = mod(iTime*4.,20.); T = min(T,20.-T);
    vec2  R = iResolution.xy,
          U = 1.3*( u - .5*R ) / R.y + vec2(0,.45),
          S = vec2(.5/s,0), Y = vec2(0,1);

    for ( O=vec4(1); i < T; i++ ) {
        a = -cross( U-S, vec2(-.5,s) ),           // distance to right border
        b =  cross( U+S, vec2( .5,s) );           // distance to left border
        if ( U.y<0. || a < 0. || b < 0. ) return; // out of triangle
        U = U.y > .67 ? ( U-.67*Y ) *3.           // top triangle
          :  b  > .67 ? ( U-.67*S ) *3.           // right triangle
          :  a  > .67 ? ( U+.67*S ) *3.           // left triangle
          :    (U-.33*Y) *rot(-.524) / (.67*s) + .33*Y; // central triangle
        O *= vec4(.7,.8,.9,0);
    }
}

// Heavely inspired by "fractal xxx gaz" -> https://www.shadertoy.com/user/gaz/
mat2 rot(float v) {
    float a = cos(v);
    float b = sin(v);
    return mat2(a,b,-b,a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 rd = normalize(vec3(uv*2.-1., 2.));
    vec3 ro = vec3(0.,0.,-.5);
    float t = 0.;

    vec3 seed = hash3( uint(iTime/10.) + 3u );
    vec3 seed2 = hash3( uint(iTime/10.) );
    float time = mod(iTime, 10.);
    float fade = smoothstep(0.,1., time) * smoothstep(10.,9.5, time);


    vec3 col = vec3(0.);
    for(int i=0; i<80; i++) {
        vec3 p = ro + rd * t;
        p.xz = rot(iTime*0.02+0.3) * p.xz;
        p.xy = rot(-iTime*0.03-.2) * p.xy;
        float s = 2., r2;
        for(int j=0; j<8; j++) {
            r2 = (fade*1.7)/min(dot(p,p),1.6);
            p = abs(p*r2) - (seed+.1)*5.;
            s *= r2;
        }

        float d = length(p.xy)/s;
        t += d*.5;
        col += vec3(cos(vec3(1.,2.,3.)-iTime*1.5*(seed2.g*2.-1.) + log(s))*.5+.5) * 0.03 * exp(-.5*d*float(i*i));
    }

    fragColor = vec4(col * fade,1.0);
}

