          :  a  > .67 ? w/=3., ( U+.67*S ) *3.           // left triangle
          :   ( w*=.67*s, (U-vec2(0,.33)) *rot(-.524) / (.67*s) + vec2(0,.33) ); // central triangle
    }
    a = -cross( U-S, vec2(-.5,s) ),
    b =  cross( U+S, vec2( .5,s) );
    O += min(U.y,min(a,b)) *w; // +.5
}
/**/

/* I tried to make my own volumetric raymarcher for arbitrary shapes. 
*  It's a naive implementation consisting of a basic raymarcher extended with an inside/outside 
*  awareness that collects density & light when inside a shape (distance field < 0).
*  I think it can be optimized by using fixed steps when marching inside a volume.    */

#define MAX_ITER 300.
#define MAX_DIST 8.
#define ABSORPTION 17.

#define hash33(p) fract(sin( (p) * mat3( 127.1,311.7,74.7 , 269.5,183.3,246.1 , 113.5,271.9,124.6) ) * 43758.5453123)

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// noise and fbm are from https://www.shadertoy.com/view/XtS3DD
float noise(in vec3 p) {
    vec3 ip = floor(p);
    vec3 fp = fract(p);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec2 rz = textureLod(iChannel0, (tap+0.5)/256.0, 0.0).yx;
	return mix(rz.x, rz.y, fp.z);
}

float fbm(in vec3 x) {
    x += vec3(iTime*.015, iTime*.035, 0.);

    float rz = 0.;
    float a = .35;
    for (int i = 0; i<4; i++) {
        rz += noise(x)*a;
        a*=.35;
        x*= 4.;
    }
    return rz - .25;
}

// signed distance to volume
float vol(vec3 p) {
    p.y -= .2;
    float r = fbm(p) + p.y*.4 + .08 + .05*max(0., 1.-length(p));
    float sphere = length(p) - 1.;
    return max(sphere, r*.9);
}

// get light visibility at point
float sampleLight(vec3 ro) {
    vec3 rd = normalize(-ro + vec3(0.,.2,0.));
    float t = 0., visibility = 1., stepSize = 0.005;

    for (int i = 0; i < 20; i++) {
        vec3 p = ro + t*rd;
        float d = vol(p);
        t += max(stepSize, d) + hash33(p).x*.002;
        if (d < 0.) visibility *= exp(-stepSize*ABSORPTION);
    }

    return visibility;
}

void initRayOriginAndDirection(vec2 fragCoord, inout vec3 ro, inout vec3 rd) {
    vec2 uv = ( fragCoord - .5*iResolution.xy )/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    float d = 1.75;
    ro = vec3(d*cos(iTime*.1), 1.5, d*sin(iTime*.1));
    ro.xz *= rot(-m.x*2.);

    float z = iMouse.z == 0. ? 1.4 : 1.2 + m.y;

    vec3 f = normalize(-ro),
         r = normalize(cross(vec3(0,1,0), f)),
         u = cross(f,r),
         c = ro+f*z,
         i = c + uv.x*r + uv.y*u;

    rd = normalize(i-ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 ro, rd;

    initRayOriginAndDirection(fragCoord, ro, rd);

    float visibility = 1.;
    vec3 vColor = vec3(0.), color = vec3(0.,0.,.1);

    float t = 0., eps = 0.0005;
    float inside = vol(ro) < 0. ? 1. : 0.;

    for (float i = 0.; i < MAX_ITER; i++) {
        vec3 p = ro + t*rd;

        float d = vol(p);
        t += abs(d) + 0.001*t;

        bool isInsideVolume = mod(inside, 2.) > .5;

        if (!isInsideVolume) { 
            if (d < eps) {  
                inside++; t += eps*10.; // enter volume
            }
        } else {    
            if (d > -eps) {  
                inside++; t += eps*10.; // exit volume
            } else {
                // sample density & light
                float prev = visibility;
                visibility *= exp(-abs(d)*ABSORPTION);

                float absorption = prev - visibility;
                vColor += absorption * sampleLight(p) * (vec3(1.3, 1.,.8) + fbm(p)*2.);

                if (visibility <= .01 || dot(vColor, vColor) >= 3.) break;         
            }
        }

        if (t > MAX_DIST) break;
    }

    color = vColor + color * visibility;
    fragColor = vec4(color,1.0);
}

