// Another cloud shader

// reduces if too slow
const float frames = 3.;

// global
float glow;

// snippets
#define R iResolution.xy
#define N(a,b,c) normalize(vec3(a,b,c))
#define ss(a,b,t) smoothstep(a,b,t)
mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float gyroid (vec3 seed) { return dot(sin(seed),cos(seed.yzx)); }

// noise
float fbm (vec3 seed)
{
    float result = 0., a = .5;
    for (int i = 0; i < 8; ++i, a/=2.)
    {
        result += abs(gyroid(seed/a))*a;
    }
    return result;
}

// signed distance function
float map(vec3 p)
{
    float dist = 100.;

    // cloud
    vec3 seed = p*.4;
    seed.z += iTime*.1;
    float noise = fbm(seed);
    dist = length(p) - .5 - noise*1.;

    // lightning
    const float count = 4.;
    float a = 1.;
    float t = iTime*.2 + noise*.5;
    float r = .1+.2*sin(iTime+p.x);
    float shape = 100.;
    for (float i = 0.; i < count; ++i)
    {
        p.xz *= rot(t/a);
        p.xy *= rot(t/a);
        p = abs(p)-r*a;
        shape = min(shape, length(p.xz));
        a /= 1.8;
    }
    glow += .002/shape;
    dist = min(dist, shape);

    return dist*.8;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;
    vec3 color = vec3(0);

    // layers
    for (float f = 0.; f < frames; ++f)
    {
        // blue noise scroll by iq https://www.shadertoy.com/view/tlySzR
        ivec2 p = ivec2(fragCoord);
        p = (p+(iFrame*196+int(f))*ivec2(113,127)) & 1023;
        vec3 blu = texelFetch(iChannel0,p,0).xyz;

        // coordinates
        vec3 pos = vec3(0,0,7);
        vec3 ray = normalize(vec3(uv,-3));
        ray.xy += blu.xy * ss(.5,8.,length(uv)); // blur edge
        pos += ray * blu.z * 4.; // pre start

        vec3 tint = vec3(0);
        glow = 0.;

        // raymarch
        const float count = 40.;
        float maxDist = 10.;
        float steps = 0.;
        float total = 0.;
        for (steps = count; steps > 0.; --steps) {
            float dist = map(pos);
            if (dist < .001*total || total > maxDist) break;
            dist *= 0.9+0.1*blu.z; // dithering
            ray.xy += blu.xy*total*.001; // depth of field
            pos += ray * dist;
            total += dist;
        }

        // shading
        float shade = steps/count;
        if (shade > .1 && total < maxDist) {

            // NuSan https://www.shadertoy.com/view/3sBGzV
            vec2 noff = vec2(.2*pow(length(uv),2.),0);
            vec3 normal = normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));

            // color palette https://iquilezles.org/www/articles/palettes/palettes.htm
            tint = .8+.5*cos(vec3(1,2,3)*6.1 + pos.y*1. + normal.z*3.);

            // backlight
            tint *= dot(normal, ray)*.5+.5;
        }

        // bloom
        tint += glow*.5;

        // average
        color += tint/frames;
    }

    fragColor = vec4(color, 1);
}

////////////////////////////////////////////////////////////////////////////////
//
// Kochawave! mla, 2022.
//
// Thanks to Fabrice for reference: https://arxiv.org/pdf/2210.17320.pdf
//
// This one needs to have variable recursion depths (down to a certain
// segment length - this should be resolution dependent).
// Inspired by https://www.shadertoy.com/view/cdsGRj by FabriceNeyret2
//
// Mouse down shows segments actually drawn around mouse position
//
////////////////////////////////////////////////////////////////////////////////


#define C(turns,offset) (((turns) << 16) | ((offset) & 0xffff))
const int A = 0, END = -1;
const int STAR = 1, SLASH = 2, PLUS = 3;
const int program[5] = int[]( C(0,A), C(STAR,A), C(SLASH,A), C(PLUS,A), C(0,END) );

const int MAXDEPTH = 10;

vec2 p = vec2(0);     // The drawing point
vec2 point = vec2(100); // The moving point
float plen = 0.0;     // Cumulative path length

int wind = 0; // Compute winding number here
void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) wind++;
  } else {
    if (r.y <= 0.0 && t < 0.0) wind--;
  }
}

float drawsegment(vec2 pos, float seglen, float theta, float t) {
  vec2 seg = seglen*vec2(cos(theta),sin(theta));
  if (t >= plen && t < plen+seglen) point = p + (t-plen)/seglen*seg;
  vec2 q = p + seg;
  float d = segment(pos,p,q);
  polywind(pos,p,q);
  plen += seglen;
  p = q;
  return d;
}

vec2 map(vec2 pos, float factor) {
  pos = (2.0*pos - iResolution.xy)/iResolution.y;
  pos.y += 0.8;
  pos *= factor/3.0;
  pos.x += 0.5*factor;
  return pos;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float factor = 3.0;// Linear scale factor at each level.
  int maxdepth = MAXDEPTH;
  //maxdepth = 1+int(iTime)%MAXDEPTH;
  vec2 pos = map(fragCoord,factor);
  vec2 pos2 = pos;
  if (iMouse.z > 0.0) pos2 = map(iMouse.xy,factor);
  int stack[MAXDEPTH];
  int depth = 0;
  stack[depth++] = 0;
  float t = mod(iTime,4.0*pow(4.0/factor,float(maxdepth-1))); // Moving point
  float d = 1e8;
  float seglen = 1.0;
  float theta = 0.0;    // Cumulative angle
  while (depth > 0) {
    int instr = program[stack[depth-1]++];
    // Decode instruction
    int turns = instr>>16;
    int code = instr<<16>>16;
    //theta += float(turns)*angle; // Apply turn
    if (turns == STAR) {
      theta += PI/6.0;
      seglen *= sqrt(3.0);
    } else if (turns == SLASH) {
      theta -= 5.0*PI/6.0;
      seglen /= sqrt(3.0);
    } else if (turns == PLUS) {
      theta += 2.0*PI/3.0;
    }

    if (code == END) {
      depth--;    // Terminate level & unwind
      seglen *= factor;
    } else if (depth == maxdepth || seglen < 0.01) {
      d = min(d,drawsegment(pos,seglen,theta,t)); // Segment
    } else {
      // Work out where we would end up if we did the full recursion
      // For Kochawave, segment length and the angle don't change.
      vec2 seg = seglen*vec2(cos(theta),sin(theta));
      vec2 newpoint = p+seg; // Where we end up
      if (distance(pos2,p+0.5*seg) > 0.75*length(seg)) {
        polywind(pos,p,p+seg);
        // Show skipped segments on mouse down.
        if (iMouse.z > 0.0) d = min(d,segment(pos,p,newpoint));
        p = newpoint;
        plen += seglen*pow(4.0/factor,float(maxdepth-depth));
      } else {
        stack[depth++] = code;  // Recurse
        seglen /= factor;
      }
    }
  }
  vec2 offset = vec2(10,0);
  polywind(pos,vec2(0),-offset);
  polywind(pos,-offset,-offset-vec2(0,10));
  d = min(d,segment(pos,vec2(0),-offset));
  polywind(pos,p,p+offset);
  polywind(pos,p+offset,p+offset-vec2(0,10));
  d = min(d,segment(pos,p,p+offset));
  float px = fwidth(pos.x);
  vec3 col = vec3(smoothstep(0.0,px,d));
  if ((wind&1) == 0) col *= 0.8;
  //col = mix(vec3(1,0,0), col,smoothstep(0.0,px,distance(pos,point)-0.01*factor/3.0));
  fragColor = vec4(col,1);
}

