// Refactored fork of "Koch Snowflake Optimized" by mla. https://shadertoy.com/view/csX3Rs
// ( Uses an explicit stack to keep track of the recursion + short-cuts to draw only what's necessary )
// I replace program symbols by true segments lengthes and angles.

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0., 1.);
  return length(p-b*h);
}

vec2 p = vec2(0);     // The drawing point

float drawsegment(vec2 U, float seglen, float a) {
  vec2 seg = seglen*vec2(cos(a),sin(a)),
         q = p + seg;
  float  d = segment(U,p,q);
  p = q;
  return d;
}

void mainImage(out vec4 O, vec2 u) {
    float END = -1., factor = 3.,    // scaling factor betweel levels
#if 0                   // von Koch snowflakes: F  F+F--F+F
    program[] = float[] (0.,1., 1.05,1., -2.1,1., 1.05,1., 0.,END);// (angle, length)
#elif 0                 // Kochawave
    program[] = float[] (0.,1., .524,1.732, -2.62,1., 2.1,1., 0.,END);
#else                   // interpolation
    X = 1.-.5*cos(iTime), S = .866,             // von Koch to Kochawave
 // X = .5 - cos(iTime) , S = .866,             // left to right Kochawave
    program[] = float[] ( 0., 1.,               // (angle, length)
                          atan(S,X), sqrt(S*S+X*X), 
                          atan(X,S)-atan(X-1.,S)-3.14, sqrt(S*S+(X-1.)*(X-1.)), 
                          atan(X-1.,S)+1.57,1., 
                          0., END );
#endif 
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y + vec2(1.5,.8);
    int maxdepth = 4, stack[6], depth = -1;     // stack: pos in program + seg length
    float lens[6];
    stack[++depth] = 0; lens[depth] = 1.;
    float d = 1e8, seglen = 1., theta = 0.;     // Cumulative angle

    while (depth >= 0) {
        float len = program[2*stack[depth] +1]; // len
        theta +=    program[2*stack[depth]++];  // turns
        if (len == END)
            seglen = lens[depth--];             // Terminate level
        else   
        if (depth == maxdepth)  
              d = min(d,drawsegment(U,seglen*len,theta)); // Draw segment
        else {
          vec2 seg = seglen*len*vec2(cos(theta),sin(theta));
          if (distance(U,p+.5*seg) > .5*length(seg))
              p += seg;                         // skip unseen segments
          else
              stack[++depth]=0, lens[depth]=seglen, seglen *= len/factor; // Recurse
        }
    }
    O =  vec4(smoothstep(0.,2./R.y,d));
}

