// The sea of LEGOS
//
// While I was creating another LEGO shader I decided
// to play around with domain repetition and ended up 
// creating something that reminded me of a sea so 
// I pushed the idea further and actually made a sea. 
//
// The ripples are actually a very simple function, its a sine wave whose domain is clamped and
// shifted over time. You can combine multiple similar functions and create the waves.
//
// The animation will change from smooth to stop-motion like
// and then back to smooth every now and then because I explored
// both effects and was torn between them so I decided to leave both :).
//
// I hope you enjoy it!
// ----------------------------------------

vec4 sdScene(vec3 p);

vec4 rayMarch(vec3 ro, vec3 rd, out bool hit) {

    vec4 res = vec4(-1.); // color = xyz, distance = w 

    float t = 0.;
    for (int i = 0; i < MAX_MARCH_STEPS && t<MAX_MARCH_DIST; ++i) {

        vec4 ds = sdScene(ro + rd*t);   
        if(ds.w*ds.w < 1e-6) {
            res = vec4(ds.xyz, t);
            hit = true;
            break;
        }

        t += ds.w;
    }

    return res;
}

// Adapted from this shader by blackle: https://www.shadertoy.com/view/3lcBD2
vec2 edge(vec2 p, float l) {
    vec2 p2 = abs(p);
    if (p2.x > p2.y) return vec2((p.x < 0.) ? -l : l, 0.);
    else             return vec2(0., (p.y < 0.) ? -l : l);
}

float ripple(float x, float sc, float at, float t) {

    // First wave
    float w1 = sin(2.*clamp(x - t, -2.*PI*0.5, 0.));

    // Second wave
    float w2 = (sin(2.*clamp(x - t, -4.5*PI*0.5, -2.5*PI*0.5))*0.5 + 0.5)*0.7;

    return (w1 + w2)*exp(-abs(x)*at)*sc;
}

float wave(float x, float z, float t) {
    float ir = cos(z/5.)*0.5;
    x += ir;
    return exp(-max(0., (x - t)*2.))*(cos(clamp(x - t, -PI, 0.))*0.5 + 0.5);
}

vec4 sdScene(vec3 p) {

    // Repeat domain on the X and Z axis 
    float domainRepeat = BRICK_WIDTH*1.06;
    vec3 p2 = p;
    p2.xz = mod(p2.xz + 0.5*domainRepeat, domainRepeat) - 0.5*domainRepeat;
    vec2 s = (p.xz - p2.xz);
    vec2 e = edge(p.xz - s, domainRepeat);
    vec2 s2 = s + e;

    // Base water movement
    float h = (1. - texture(iChannel1, s.xy*0.1 - vec2(iTime*0.02)).r)*0.7;

    // Wave
    float wt = mod(iTime, 20.);
    float wh = max(.7, hash1(iTime - wt)*1. + 0.4);
    float wv = wave((-s.y+s.x*0.2)*0.4 + 4.*PI, s.x, wt*2.)*wh;
    h += wv;

    const float MAX_HEIGHT = 3.;

    float ds = 1000.;
    vec3 col;

    // Falling brick
    {
        // Frequency of falling brick
        float time = iTime*5.;
        float t = mod(time,10.); 
        float ph = time - t;

        // Random value
        vec2 r = hash21(round(ph + 100.));
        float r2 = r.x;

        // Place where brick will fall
        r.x *= 2.*PI; // Random angle
        r.y *= 2.0 - 1.0; // Random offset
        vec3 pos = vec3(cos(r.x)*3., 0., sin(r.x)) * (7.*r.y);

        // Brick transformations
        vec3 p3 = p;
        p3 -= vec3(0., 10.5 - pow(t, 0.5)*15., 0.) + pos;
        p3.xy *= rot(t*PI + ph);
        p3.xz *= rot(t*PI);
        ds = sd1by1Brick(p3);
        col = vec3(1., r2, 0.); 

        // Ripple
        float x = length(pos.xz - s.xy);
        h += ripple(x, 2., .3, t*1.6);
    }

    /*
    // Falling brick 2
    {
        float time = iTime*5. - 5.;
        float t = mod(time,20.); 
        float ph = time - t;

        vec2 r = hash21(round(ph + 300.));
        r.x *= 2.*PI; // Random angle
        r.y *= 2.0 - 1.0; // Random offset

        vec3 pos = vec3(cos(r.x)*3., 0., sin(r.x)) * (12.*r.y);
        vec3 p3 = p;
        p3 -= vec3(0., 10.5 - pow(t, 0.5)*20., 0.) + pos;
        p3.xy *= rot(t*PI + ph);
        p3.xz *= rot(t*PI);
        ds = min(ds, sd1by1Brick(p3)*1.);
        col = vec3(1., 1., 0.);
        float x = length(pos.xz - s.xy);
        h += ripple(x, 1., .3, t*1.6);
    }

    // Falling brick 3
    {
        float time = iTime*5. - 10.;
        float t = mod(time,20.); 
        float ph = time - t;

        vec2 r = hash21(round(ph + 600.));
        r.x *= 2.*PI; // Random angle
        r.y *= 2.0 - 1.0; // Random offset

        vec3 pos = vec3(cos(r.x)*3., 0., sin(r.x)) * (3.*r.y);
        vec3 p3 = p;
        p3 -= vec3(0., 10.5 - pow(t, 0.5)*15., 0.) + pos;
        p3.xy *= rot(t*PI + ph);
        p3.xz *= rot(t*PI);
        ds = min(ds, sd1by1Brick(p3)*1.);
        col = vec3(1., 0., 0.);
        float x = length(pos.xz - s.xy);
        h += ripple(x, 2., .4, t*1.6);
    }

    // Falling brick 4
    {
        float time = iTime*5. - 16.;
        float t = mod(time,20.); 
        float ph = time - t;

        vec2 r = hash21(round(ph + 1000.));
        r.x *= 2.*PI; // Random angle
        r.y *= 2.0 - 1.0; // Random offset

        vec3 pos = vec3(cos(r.x)*3., 0., sin(r.x)) * (3.*r.y);
        vec3 p3 = p;
        p3 -= vec3(0., 10.5 - pow(t, 0.5)*15., 0.) + pos;
        p3.xy *= rot(t*PI + ph);
        p3.xz *= rot(t*PI);
        ds = min(ds, sd1by1Brick(p3)*1.);
        col = vec3(1., 1., 0.);
        float x = length(pos.xz - s.xy);
        h += ripple(x, 3., .5, t*1.6);
    }*/

    if ( mod(floor(iTime/15.),2.) != 0.) h = round(h*3.)/3.; 

    // Ocean distance
    float ds2 = sd1by1PlateInf(p - vec3(s.x, h, s.y));
    ds2 = min(ds2, sd1by1PlateInf(p - vec3(s2.x, MAX_HEIGHT, s2.y)));    
    if (ds2 < ds) {
        col = mix(vec3(5.,63.,79.)/255., vec3(135.,159.,168.)/255.*2., smoothstep(0.65, 1.0, ((h/MAX_HEIGHT)*0.5 + 0.5)));
        float tones = 20.;
        col = ceil(col * tones) / tones;
        col += hash12(s.xy*0.1)*0.02;
        ds = ds2;
    }

    return vec4(col, ds - 0.2*PIECES_SCALE);

}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(vec3 p) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdScene( p + k.xyy*h ).w + 
                      k.yyx*sdScene( p + k.yyx*h ).w + 
                      k.yxy*sdScene( p + k.yxy*h ).w + 
                      k.xxx*sdScene( p + k.xxx*h ).w );
}

// iq's noise
float pn( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel2, (uv+ 0.5)/256.0, 0.0 ).yx;
	return -1.0+2.27*mix( rg.x, rg.y, f.z );
}

// duke's stars
vec3 stars(vec3 rd) {
    vec3 stars = vec3(pn(rd*100.0)*0.4+0.5);
    vec3 starbg = vec3(0.0);
    starbg = mix(starbg, 
        vec3(255.,247.,90.)/255., 
        smoothstep(0.99, 1.0, stars)
        );
    return starbg;
}

vec3 renderScene(vec3 ro, vec3 rd, vec3 sky) {

    vec3 col = sky;
    vec4 obj = vec4(0., 0., 0., 40.);

    bool hit = false;
    obj = rayMarch(ro, rd, hit);

    if (hit) {
        col = vec3(0.);

        vec3 p = ro + rd*obj.w;
        //vec3 p = obj.xyz;
        vec3 n = calcNormal(p);
        vec3 objCol = obj.xyz;

        float R0 = pow((1. - 1.333)/(1. + 1.333),2.);        

        // Side Light
        {
            vec3 ld = normalize(vec3(0.0, 0.3, 1.0));
            float dif = max(0., dot(n, ld));

            vec3 hlf = normalize(-rd + ld);
            float spe = pow(max(0., dot(n, hlf)), 16.);

            float fr = R0 + (1. - R0)*pow(1. - dif, 5.);

            col += spe * vec3(250.,193.,138.)/255. * dif;
            col += fr  * dif;
        }

        // Sky light
        {
            vec3 ld = normalize(vec3(0.0, 1.0, 0.0));
            float dif = max(0., dot(n, ld));

            col += dif * objCol * 0.9;
            col += sky * .2;
        }

        col = pow(col, vec3(1./.4545));
        col = mix(sky, col, (MAX_MARCH_DIST - obj.w) / MAX_MARCH_DIST);

    } else {    
        obj.w = 40.;
        col = col = mix(sky, col, (MAX_MARCH_DIST - obj.w) / MAX_MARCH_DIST);
        col += stars(rd);// * sky * 5.;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec3 origin = vec3(0.);

    vec3 ro = vec3(2.,7., -12.);
    //ro.x += sin(iTime*0.2)*0.3;
    //ro.y += cos(iTime*0.1)*1.; 

    vec3 rd = rayDir(ro, origin, uv);

    vec3 sky = mix(vec3(250.,193.,138.)/255.*0.4, vec3(72.,74.,93.)/255.*0.4, uv.y*0.5 + 0.5);

    vec3 col = renderScene(ro, rd, sky);

    // Contrast
    col.r = smoothstep(0.11, 1., col.r);
    col.b = smoothstep(0.1, 1., col.b);
    col.g = smoothstep(0.11, 1., col.g);

    // Vignette
    col *= pow(-length(uv)+2., 0.6);

    // Gamma correct ^(1/2.2)
    col = pow(col, vec3(.4545));

    // Output to screen
    fragColor = vec4(col,1.0);
}

