#define PI 3.14159265359

#define PIECES_SCALE 0.1

#define PLATE_HEIGHT (3.2 * PIECES_SCALE)
#define BRICK_WIDTH (8. * PIECES_SCALE)
#define BRICK_HEIGHT (9.6 * PIECES_SCALE)
#define STUD_HEIGHT (1.6 * PIECES_SCALE)
#define STUD_RADIUS (2.4 * PIECES_SCALE)

float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}


vec3 rayDir(vec3 ro, vec3 origin, vec2 uv) {

    vec3 d = normalize(origin - ro);

    vec3 r = normalize(cross(vec3(0.,1.,0.), d));
    vec3 u = cross(d, r);

    return normalize(d + r*uv.x + u*uv.y); 
}

mat2 rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

bool intersectSphere(vec3 ro, vec3 rd, vec3 p, float r) {
    vec3 oc = ro - p;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - r*r;
    float h = b*b - c;
    if( h<0.0 ) return false;
    return true;
}

float hash1( float n )
{
    return fract( n*17.0*fract( n*0.3183099 ) );
}

/* ----- Iq's SDF functions (https://iquilezles.org/articles/distfunctions/) ---------- */

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdBox2( vec3 p, vec3 b )
{
  p.xz = abs(p.xz);
  vec3 q = p - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
  // return length(max(q,0.0)) + min(max(q.x,q.y,q.z),0.0) -> Pyramid top
}


float sdCappedCylinder( vec3 p, float r, float h)
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float dot2(in vec2 v) {
    return dot(v,v);
}

float bb( vec2 p, vec2 b )
{
  return length(max(abs(p)-b,0.0));
}

// Awsome LEGO logo by mmalex: https://www.shadertoy.com/view/MsX3W2
float lego(vec2 uv) { // x is -2 to 2
    uv.x+=uv.y*-0.1; // italic
    float oldx=uv.x;
    uv.x=fract(uv.x)-0.5; // letter repeat
    if (abs(oldx)>2.0) return 0.0; // clip!
    float l;
    if( oldx<0.0) {
        // l and e
        float e0=bb(uv-vec2(-0.15,0.0),vec2(0.2,0.0)); // cross of e 
        if (oldx>-1.0) uv.y=-abs(uv.y); else e0=1.0;
        float l0=bb((uv)-vec2(0.0,-0.75),vec2(0.35,0.0)); // bottom of l
        float l1=bb((uv)-vec2(-0.35,0.0),vec2(0.0,0.75)); // left of l                                              
        l0=min(l0,e0);
        l=min(l0,l1);                                        
    } else {
        l=abs(bb(uv,vec2(0.1,0.5))-0.25); // round o
        if (oldx<1.0) {
            // g - ugh nasty
            if (uv.x>0.0 && uv.y>0.0 && uv.y<0.5)                                    
                            l=bb((uv)-vec2(0.35,0.6),vec2(0.0,0.1));
            float e0=bb(uv-vec2(0.2,0.0),vec2(0.15,0.0));
            l=min(l,e0);
        }
    }              
    return smoothstep(0.1,0.05,l);   
}

/* --- LEGO Common SDFs ---- */
float sdStud(vec3 p) {
    float ds = sdCappedCylinder(p - vec3(0.0, lego(p.xz*10.)*0.006, 0.0), STUD_RADIUS, .5*STUD_HEIGHT);  
    return ds;
}

float sd1by1Plate(vec3 p) {
    float ds = sdBox(p, vec3(.5*BRICK_WIDTH, .5*PLATE_HEIGHT, .5*BRICK_WIDTH ));
    ds = min(ds, sdStud(p - vec3(0.0, .5*PLATE_HEIGHT + .5*STUD_HEIGHT, 0.0)));
    return ds;
}

float sd1by1PlateInf(vec3 p) {
    float ds = sdBox2(p, vec3(.5*BRICK_WIDTH, .5*PLATE_HEIGHT, .5*BRICK_WIDTH ));
    ds = min(ds, sdStud(p - vec3(0.0, .5*PLATE_HEIGHT + .5*STUD_HEIGHT, 0.0)));
    return ds;
}

float sd1by1Brick(vec3 p) {
    float ds = sdBox(p, vec3(.5*BRICK_WIDTH, .5*BRICK_HEIGHT, .5*BRICK_WIDTH ));
    ds = min(ds, sdStud(p - vec3(0.0, .5*BRICK_HEIGHT + .5*STUD_HEIGHT, 0.0)));
    return ds;
}

