vec2 laplacianAB(in vec2 xy) {
    vec2 sum = vec2(0.);
    vec2 px = 1./iResolution.xy;
    px *= 1.;
    int range = 1;
    for (int i=-range;i<range+1;i++){
        for (int j=-range;j<range+1;j++){
            vec2 offset = vec2(float(i),float(j))*px;
            float factor = 0.05;
            if (i==0||j==0) factor = 0.2;
            if (i==0&&j==0) factor = -1.;
            if (i<-1||j<-1) factor = 0.02;
            if (i>1||j>1) factor = 0.02;
            sum += texture(iChannel0,xy+offset).ab*factor;
        }
    }
    px *= 3.;
    range = 1;
    for (int i=-range;i<range+1;i++){
        for (int j=-range;j<range+1;j++){
            vec2 offset = vec2(float(i),float(j))*px;
            float factor = 0.05;
            if (i==0||j==0) factor = 0.2;
            if (i==0&&j==0) factor = -1.;
            if (i<-1||j<-1) factor = 0.025;
            if (i>1||j>1) factor = 0.02;
            sum += texture(iChannel0,xy+offset).ab*factor;
        }
    }
    return sum / 2.;
}

vec2 setAB(in vec2 xy, in float cdist) {

    float dA = 1.;
    float dB = .25;
    float feed = 0.055;
    float k = 0.065;

    float a = texture(iChannel0,xy).a;
    float b = texture(iChannel0,xy).b;
    vec2 lapAB = laplacianAB(xy);
    return vec2(
        a +
        dA * lapAB.x -
        a * b * b +
        feed * (1. - a),
        b +
        dB * lapAB.y +
        a * b * b -
        (k + feed) * b
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv = iMouse.xy/iResolution.xy;
    vec2 uvc = (fragCoord*2. - iResolution.xy)/iResolution.y;
    vec2 muvc = (iMouse.xy*2. - iResolution.xy)/iResolution.y;
    float mdist = distance(uvc,muvc);
    float cdist = length(uvc);

    uv -= 0.5;
    uv *= vec2(16.,9.);
    uv *= 0.9995;
    uv /= vec2(16.,9.);
    uv += 0.5;
    fragColor.ab = setAB(uv,cdist);
    fragColor.b = mix(fragColor.b,texture(iChannel1,uv,4.).b*1.1,0.01);
    fragColor *= 1. + cnoise(vec3(uv.xy * 2., iTime * 0.4))*0.015;
    if (iMouse.z > 0.5) {
        if (mdist<0.05) { fragColor.b = 1.; fragColor.a = 0.; }
    }

    // init animation
    if (iFrame < 67) {
        float sdist1 = distance(uvc, vec2(iTime-1.,0.));
        float sdist2 = distance(uvc, vec2(-iTime+1.,0.));
        fragColor.b = 1.-step(iTime*0.1,sdist1);
        fragColor.b += 1.-step(iTime*0.1,sdist2);
        fragColor.b = clamp(fragColor.b,0.,1.);
        fragColor.a = 1.-fragColor.b;
    }
}

