            co += cos(y2)*a;
            si += sin(y2)*a;
        }
        float l = sqrt(si*si+co*co)/su;
                vec4 msk = vec4(1,0,0,0);
      //if((i%3)==0){msk = vec4(1,0,0,0);}
        if((i%3)==1){msk = vec4(0,1,0,0);}
        if((i%3)==2){msk = vec4(0,0,1,0);}
        fragColor += l*msk;
        frq *= 1.3; //2^(1/3)
    }
    fragColor *= 4.*(3./float(ww));
    //if(fragCoord.x>iResolution.x*.5){fragColor = vec4(tt.x&1U);}
}

