uvec4 read(vec2 u, vec2 m)
{
    return floatBitsToUint(texelFetch(iChannel0,ivec2(u+m),0));
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    uvec4 tt = read(u,vec2(0,0));
    float ck = -floor((sz-1.)*.5);    //center the kernel
    float ev = float(mod(sz,2.)==0.); //move and resize kernel when sz = even
    uint o = 0U;                      //sum of all bits
                          float n = (float(iFrame&1)-1.)*ev;
    for(int   t=0 ; t<tz; ++t){ float m = n*(1.-float(t&1)); float s2 = sz-float(t&1)*ev; 
    for(float i=0.; i<s2; ++i){
    for(float j=0.; j<s2; ++j){ uint r = read(u,vec2(i,j)+ck+m).x;
                                o += (r>>t)&1U;
    }}}

    uint a = +16U*8U        *(16U*7U+12U)
             +16U*8U*16U*8U *(1U<<8);      
    o = ((tt.x)<<1U) | ((a>>(o&31U))&1U);
    tt = uvec4(o,(tt.x>>31)|(tt.y<<1),
                 (tt.y>>31)|(tt.z<<1),
                 (tt.z>>31)|(tt.w<<1));
    fragColor = uintBitsToFloat(tt);
    if(iFrame==0||iMouse.z>.5)
    {
        vec2  v = (u/iResolution.xy)-.5;
        float r = fract(cos(dot(u,vec2(1.76543,iTime+9.)))*5467.5678)*11111.;
              r*= step(dot(v,v),nz);
        fragColor = vec4(r);
    }
}

image_code:
#define S(a,b,t) smoothstep(a,b,t)
#define ROT -0.785398
#define ZOOM .25
#define STAR_SPEED 4.

float N21(vec2 p) {
    p = fract(p*vec2(233.34, 851.73));
    p += dot(p, p+23.45);
    return fract(p.x * p.y);
}

float DistLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/ dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}


float DrawLine(vec2 p, vec2 a, vec2 b) {
    float d = DistLine(p, a, b);
    float m = S(.0025, .00001, d);
    float d2 = length(a-b);
    m *= S(1., .5, d2) + S(.04, .03, abs(d2-.75));
    return m;
}

float ShootingStar(vec2 uv) {    
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float h = N21(id);

    float line = DrawLine(gv, vec2(0., h), vec2(.125, h));
    float trail = S(.14, .0, gv.x);

    return line * trail;
}

float rand(in vec2 st) {
  vec2 r = fract(sin(st) * 2.7644437);
  return fract(r.y * 276.44437 + r.x);
}

float particles(in vec2 st) {
  float r = rand(floor(st));
  return 0.01 + smoothstep(0.995, 1.0, r) * max(0.0, sin(r * 34433.0 + iTime));
}

const vec3 BLUE = vec3(0.0, 0.1, 0.2);

#define p(st) particles(st)
vec3 avg(in vec2 st, in float a) {
  vec2 A = vec2(0.0, a);
  return BLUE * (p(st) + p(st + A) + p(st + A.yx) + p(st - A) + p(st - A.yx));
}

vec3 stars(in vec2 st) {
  vec3 color = vec3(0.0);
  for (float i = 8.0; i > 0.0; --i) color += mix(color, avg(st, i), 1.5);
  return color + p(st);
}

#define scale 150.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float t = iTime * STAR_SPEED;
    vec2 rv1 = vec2(uv.x - t, uv.y + t);
    rv1.x *= 1.1;

    float r = 3. * ROT;
    float s = sin(r);
    float c = cos(r);
    mat2 rot = mat2(c, -s, s, c);
    rv1 *= rot;
    rv1 *= ZOOM * .9;

    vec2 rv2 = uv + t * 1.2;
    rv2.x *= 1.1;

    r = ROT;
    s = sin(r);
    c = cos(r);
    rot = mat2(c, -s, s, c);
    rv2 *= rot;
    rv2 *= ZOOM * 1.1;

    float star1 = ShootingStar(rv1);
    float star2 = ShootingStar(rv2);


    vec3 col = vec3(clamp(star1+star2, 0., 1.));
    fragColor = vec4(col,1.0);


      vec2 st = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
      st *= scale;

    fragColor.rgb = fragColor.rgb + stars(st).rgb;
}
