const vec2 c_i = vec2(0,1);
const float PI = 3.1415926535897;
const int numberOfLeaves = 8;
const int X = numberOfLeaves/2;
vec2 c_cjg(in vec2 c) {
return vec2(c.x, -c.y);
}

vec2 c_mul(in vec2 a, in vec2 b) {
return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 c_pow(in vec2 c, int p) {
    vec2 temp = vec2(1.0,0.0);
for (int i = 0; i < p; ++i) {
temp = c_mul(temp, c);
}
    return temp;
}
vec2 c_div(in vec2 a, in vec2 b) {
    return
        vec2(a.x*b.x + a.y*b.y,
             a.y*b.x - a.x * b.y)
        / (b.x * b.x + b.y * b.y)
        ;
}
vec2 c_exp(in vec2 c) {
    return exp(c.x) * vec2(cos(c.y),sin(c.y));
}
//uses the even-odd split formula, there are probably better ways
vec2 c_sin(in vec2 c) {
    return
        c_div(
        c_exp(c_mul(c_i , c))
            -c_exp(c_mul(-c_i , c))
            ,
            2.*c_i

        );
}
vec2 c_cos(in vec2 c) {
    return
        c_div(
        c_exp(c_mul(c_i , c))
            +c_exp(c_mul(-c_i , c))
            ,
            2. * vec2(1.,0.)

        );
}
vec2 c_sinh(in vec2 c) {
    return
        c_div(
        c_exp(c)
            -c_exp(-c)
            ,
            2. * vec2(1.,0.)

        );
}
vec2 c_cosh(in vec2 c) {
    return
        c_div(
        c_exp(c)
            +c_exp(-c)
            ,
            2. * vec2(1.,0.)

        );
}

vec2 c_add(in vec2 c, float r)
{
return vec2(c.x+r, c.y);
}

vec2 func(in vec2 xy)
{
    float omega = 0.4;
    vec2 s = vec2(xy.y,xy.x) - vec2(0.50,0.450);
    float pole1 = 10.00 * cos(omega * iTime);
    float pole2 = 5.0 * sin(omega * iTime);
    vec2 preret = c_div(vec2(1,0), c_mul(c_pow(c_div(s,vec2(0.5,0)),X),c_mul(c_add(s,pole1), c_add(s,pole2))));
    vec2 ret = preret;
    return ret;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 complex_val = func(uv);
    // Time varying pixel color
    vec3 col;    
    col = 0.1 + abs(complex_val.x) *vec3(0.0,0.784,0.635) + abs(complex_val.y) * vec3(0.784, 0.635, 0.784);
    // Output to screen
    fragColor = vec4(col,1.0);
}

