#define FAR 100.
#define eps 10./iResolution.y
#define R iResolution
#define F fragCoord
#define S smoothstep
#define T iTime
#define PI acos(-1.)
#define TAU PI*2.
#define M (iMouse.xy*2.-R.xy)/R.y
#define Mz iMouse.z
#define E 2./R.y
#define ROT(a) mat2(cos(a + vec4(0,11,33,0)))
#define F5(u) (fract((u))-0.5)
#define aFract(u) abs(fract((u))-0.5)

#define L(p,u) length(u-p)//max(abs(u.x-p.x),abs(u.y-p.y))//
#define SOLID(shape,r) S(r+E,r-E,shape)

//r=radius, t = thickness,  E=epsilon  //add t to shift to edge
#define OUTLINE(shape,r,t) S(t+E,t-E,abs(shape-r+t))
//double outline for ends of a shape
    //  r = radius, ot = outline thickness
    //  rt = orig ring thickness                      
    //add rt to shift corrent
    //subtract  (rt-ot) to sprad correct
#define DOUTLINE(shape,r,ot,rt) S(ot+E*2.,ot-E*2.,abs(abs(shape-r+rt)-(rt-ot)))

//r=radius, t = thickness, b = blur   //add t to shift to edge
#define OLS(shape,r,t,b) S(t*1.25+b,t*1.25-b,abs(shape-r+t))
#define OUTSHADOW(shape,r,l) S(r+l,r,shape)
#define INSHADOW(shape,r,t,l) S(r+E,r-E,shape)*S(t+E,t-E-l,abs(shape-r+t/4.))
#define GLOW(shape,r,t,l) S(t+E,t-E-l,abs(shape-r+t/4.))

#define CKR(u) mod(floor((u).x)+mod(floor((u).y)+1.,2.),2.)
#define CKRF(u) max(aFract(u).x,aFract(u).y)
#define CKRO(u) S(0.47,0.48,max(aFract(u).x,aFract(u).y))

#define TX0(u) texture(iChannel0,u).rgb
#define TX1(u) texture(iChannel1,u).rgb
#define TX2(u) texture(iChannel2,u).rgb
#define TX3(u) texture(iChannel3,u).rgb

#define WHITE vec3(1.,1.,1.)
#define BLACK vec3(0.,0.,0.)
#define GREY1 vec3(0.1,0.1,0.1)
#define GREY3 vec3(0.3,0.3,0.3) 
#define GREY5 vec3(0.5,0.5,0.5)
#define GREY7 vec3(0.7,0.7,0.7)
#define GREY9 vec3(0.9,0.9,0.9) 

#define RED vec3(0.5,0.2,0.)
#define GOLD vec3(0.9,0.5,0.3)
#define BLUE vec3(0.1,0.4,0.5)

float vig(vec2 uv){
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 200.0;
    return pow(vig, 1.); 
}

float hash(float p)
{
	 return fract(sin(p*324.341)*23402.43);
}

float hash21(vec2 st){
    return fract(sin(dot(vec2(12.23,74.343),st))*43254.);  
}

mat2 rot(float a) {
    return mat2(cos(a - vec4(0,11,33,0)));
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float Gyroid(vec3 p, float scale, float thickness, float bias) {
    p *= scale;
    float d = abs(dot(sin(p), cos(p.zxy))-bias)/scale-thickness;
	return d;
}


bool checkLogoBoundingBox(vec3 p){

    return (p.x < -4.5 || p.x > 4.5 || 
            p.y < -3. || p.y > 2. || 
            p.z < -4. || p.z > 4.);

}

//float objID = 0.;
float map(vec3 p) {
    float d = length(p)-1.5;//sdBox(p, vec3(1));

    return d;//skull(p);
}

float RayMarch(vec3 ro, vec3 rd) {
	float t=0.;

    for(int i=0; i<100; i++) {
    	vec3 p = ro + rd*t;
        float d = map(p);
        t += d*0.7;
        if(t>FAR || abs(d)<0.001) break;
    }

    return t;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = map(p) - 
        vec3(
            map(p-e.xyy), 
            map(p-e.yxy),
            map(p-e.yyx)
            );

    return normalize(n);
}


vec3 camRay(vec2 uv, vec3 o, vec3 target, float zoom){

    vec3 fwd = normalize(target - o);
    vec3 uu = vec3(0.,1.,0.);
    uu = dot(target,uu) < 0.01 ? vec3(0.,0.,1.) : uu;
    vec3 right = normalize(cross(uu, fwd));
    vec3 up = cross(fwd,right);
    vec3 rd = right*uv.x + up*uv.y + fwd*zoom;
    return normalize(rd);

}

vec2 map_from_sphere(vec3 p)
{
    p = normalize(p);
    return vec2(atan(p.x,p.y)/PI/2.+0.5, acos(p.z)/PI/2.+0.5);
}

vec3 checkers(vec2 u){
   // u.x += T*0.1;
    vec2 st = u;
    u*=2.;
    //u.x +=0.14;
    u = F5(u*2.)*2.;
    u = u.x*u.y < 0. ? abs(u.yx) : abs(u);
    u = abs(u)-0.7155;//-0.8;//-0.1
    u = abs(u)-0.5;//-0.3;//-0.6

    vec3 col = GREY5*0.35-CKR(st*20.)*0.15-CKRF(st*20.)*0.03;
    col -= (CKRO(st*20.)*0.82-0.01 );
    return col;
    }

vec3 celtic(vec2 u){
   // u.x += T*0.1;
    vec2 st = u;
    u*=2.;
    //u.x +=0.14;
    u = F5(u*2.)*2.;
    u = u.x*u.y < 0. ? abs(u.yx) : abs(u);
    u = abs(u)-0.7155;//-0.8;//-0.1
    u = abs(u)-0.5;//-0.3;//-0.6

    vec3 col = GREY5*0.35-CKR(st*20.)*0.15-CKRF(st*20.)*0.03;
    col -= (CKRO(st*20.)*0.82-0.01 );
    float radius = 0.959;
    float thick = 0.08;
    float othick = 0.01;
    float bthick = 0.013;
    float blur = 0.06;
    vec3 REDNEW = pow(RED*TX1(st*4.),vec3(1.4))*2.;

    col = mix(col,BLACK,OLS(L(vec2(0.5,-0.5),u),radius,thick,blur));
    col = mix(col,REDNEW,OUTLINE(L(vec2(0.5,-0.5),u),radius,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(0.5,-0.5),u),radius,bthick,thick));
    col = mix(col,GOLD*(0.1+TX0(st*2.)),DOUTLINE(L(vec2(0.5,-0.5),u),radius,othick,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(0.5,-0.5),u),radius,bthick*0.2,thick));


    col = mix(col,BLACK,OLS(L(vec2(0.5,0.5),u),radius,thick,blur));
    col = mix(col,REDNEW,OUTLINE(L(vec2(0.5,0.5),u),radius,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(0.5,0.5),u),radius,bthick,thick));
    col = mix(col,GOLD*(0.1+TX0(st*2.)),DOUTLINE(L(vec2(0.5,0.5),u),radius,othick,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(0.5,0.5),u),radius,bthick*0.2,thick));

    col = mix(col,BLACK,OLS(L(vec2(-0.5,0.5),u),radius,thick,blur));
    col = mix(col,REDNEW,OUTLINE(L(vec2(-0.5,0.5),u),radius,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(-0.5,0.5),u),radius,bthick,thick));
    col = mix(col,GOLD*(0.1+TX0(st*2.)),DOUTLINE(L(vec2(-0.5,0.5),u),radius,othick,thick));
    col = mix(col,BLACK,DOUTLINE(L(vec2(-0.5,0.5),u),radius,bthick*0.2,thick));

    return col;
}

vec3 triplanar(vec3 p, vec3 n){
    n = abs(n);
    return mix(mix(celtic(p.yz),celtic(p.xy),n.z),celtic(p.xz),n.y);
}

// returns cubemap coordinates (FROM BIGWINGS)
// xy = uv coords for face of cube, z = cube index (-3,-2,-1, 1, 2, 3)
vec3 WorldToCube(vec3 p) {
	vec3 ap = abs(p);
    vec3 sp = sign(p);
    float m = max(ap.x, max(ap.y, ap.z));
    vec3 st;
    if(m==ap.x)
        st = vec3(p.zy, 1.*sp.x);
    else if(m==ap.y)
        st = vec3(p.zx, 2.*sp.y);
    else
        st = vec3(p.xy, 3.*sp.z);

    st.xy /= m;

    // iq version, no trig, short and sweet
   st.xy *= 3.0/(2.0+abs(st.xy));
//st.xy *= (1.45109572583 - 0.451095725826*abs(st.xy));
    return st;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 4, -1);
    vec3 light = normalize(vec3(-1.,2.,1.));
    vec3 light2 = normalize(vec3(1.,2.,-1.));
  //  light = normalize(vec3(-1.,4.,1.));
  //  ro.yz *= rot(-m.y*PI+1.+iTime);
    //ro.xz *= rot(-m.x*TAU);
   // light.yz *= rot(-m.y*PI+1.);
   // light.xz *= rot(-m.x*TAU);

    vec3 rd = camRay(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = celtic(uv.xy+iTime*0.05)*vig((F.xy/R.xy))*vec3(0.7,0.5,0.3)*0.2;
    col = mix(col,BLACK,smoothstep(0.,0.3,(1.-length(uv+vec2(-0.09,0.1))-0.5))*0.8);
    float t = RayMarch(ro, rd);

    if(t<FAR) {
        vec3 p = ro + rd * t;


        vec3 n = GetNormal(p);



        float ld = length(light-p);
        float ld2 = length(light2-p);

        p.yz *= rot(-m.y*PI+1.+iTime*0.2);
        p.xz *= rot(-m.x*TAU+iTime*0.2);

        vec3 cubeCoords = WorldToCube(p);
        /*
        vec3 CXA = WorldToCube(p) + vec3(0.,E,0.);
        vec3 CXB = WorldToCube(p) + vec3(0.,-E,0.);
        vec3 CYA = WorldToCube(p) + vec3(E,0.,0.);
        vec3 CYB = WorldToCube(p) + vec3(-E,0.,0.);
        vec3 bump = vec3(CXB-CXA,CYB-CYA,0.);*/

        float attn = 1./(0.3+ld*0.2+ld*ld);
        float diff = max(0.,dot(n,light))*0.8+0.05;
        float spec = pow(max(0.,dot(
                     reflect(n,-light
                     ),rd)),100.);


        float attn2 = 1./(0.3+ld2*0.2+ld2*ld2);
        float diff2 = max(0.,dot(n,light2))*0.8+0.05;
        float spec2 = pow(max(0.,dot(
                     reflect(n,-light2
                     ),rd)),100.);

        float ref = TX2(-reflect(n,rd)).x;   
        vec3 tp_tex = triplanar(p*0.5,n);
        vec3 sc_tex = celtic(map_from_sphere(p));

        vec3 cc_text = celtic((cubeCoords.xy*0.5+cubeCoords.z));//TX0(map_from_sphere(p)*3.);
      //  col = mix(sc_tex,tp_tex,abs(n.z)) * diff;
        vec3 col1 = 1.4*clamp(cc_text,0.,1.)*diff*1.6*attn + ref*0.0*diff*BLUE +  BLUE*spec*.08;
        vec3 col2 = 1.4*clamp(cc_text,0.,1.)*diff2*1.6*attn2*RED + ref*0.0*diff2 +  RED*spec2*.08;
        col = (col1+col2);

    }

    col = pow(col, vec3(.4545));

    fragColor = vec4(col,1.0);
}

