float getwaves(vec3 position, float dragmult){
    int iters = getwaves_directions.length();
    float result = 0.0;
    for(int i=0;i<iters;i++){
        float x = dot(getwaves_directions[i], position);

        float wave = exp(sin(x) - 1.0);
        float dx = wave * cos(x);

        result += wave;
        position += getwaves_directions[i] * dx * dragmult;
