vec3 pallette(float t){
    vec3 a = vec3(0.238, 0.028, 0.828);
    vec3 b = vec3(-1.385, 0.720, 0.720);
    vec3 c = vec3(-2.372, 1.110, 1.110);
    vec3 d = vec3(1.758, -0.422, 1.388);
    return a + b * cos(6.28318 * (c * t + d));
}

vec3 colorVariation(float timeScale, vec2 uv){
    float r = clamp(sin(iTime * timeScale) * 6.28318, 0.0, 0.1);
    float b = clamp(cos(iTime * timeScale) * 6.28318, 0.0, 0.1);

    return vec3(sin(r * uv * timeScale), sin(b * uv.y * uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float strength = 8.;
    float timeScale = 0.3;
    float iterations = 2.0;

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < iterations; i++){
        uv = fract(uv * 2.0) - 0.5;

    float d = length(uv - vec2(0.0, tan(iTime * timeScale))) * exp(-length(uv0));
    vec3 col = clamp(pallette(length(uv0) - iTime * timeScale) * abs(tan(iTime * timeScale)), 0.0, 1.0);

    d = sin(d * strength + iTime) / strength;
    d = abs(d);

    d = 0.01 / d;

    finalColor += col * d + colorVariation(timeScale, uv0);
    }

    fragColor = vec4(finalColor, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	int intx = int(fragCoord.x);
	int inty = int(fragCoord.y);
	while (intx > 0 && inty > 0) {
		int xmod = intx % 3;
		int ymod = inty % 3;

		if (xmod == 1 && ymod == 1) {
			fragColor = vec4(0.0, 0.0, 0.0, 1.0);
			return;
		}

		intx /= 3;
		inty /= 3;
	}

	// Output to screen
	fragColor = vec4(1.0, 1.0, 1.0, 1.0);
}

