#ifdef GL_ES
precision mediump float;
#endif

#define P 0.003 * GRID
#define GRID 16.0
#define PI 3.14159265359 
#define u_time iTime
#define u_resolution iResolution

vec2 rotate(vec2 v, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    mat2 rotationMatrix = mat2(c, -s, s, c);
    return rotationMatrix * v;
}

vec2 gridi(vec2 st){
    return floor(st * GRID);
}

vec2 gridf(vec2 st){
    return fract(st * GRID);
}

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

float random(float x) {
    return fract(sin(x) * 43758.5453);
}

float noise(float x) {
    float i = floor(x);
    float f = fract(x);

    float a = random(i);
    float b = random(i + 1.0);

    // Smooth interpolation
    float u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u);
}

float f2(float x){
    return noise(x/32.)*2.+noise(x/16.)+ x/128.;
}

float f3(float x){
    return floor(f2(floor(x)));
}

float f4(float x){
    return smoothstep(0.,1.,fract(x));
}

float f5(float x){
    return f3(x)*(1.-f4(x))+f3(x+1.)*f4(x);
}

float circle(vec2 st, float r, float t){
    float d = length(st);
    d = smoothstep(r - t - P, r - t, d) - smoothstep(r + t, r + t + P, d) ;

    return d;
}

float rotate_noise(float x){
    return f5(x) * PI/2.0;
}

float pattern(vec2 st, float t){
    st -= vec2(0.5, 0.5);
    st = rotate(st, rotate_noise(t));
    st += vec2(0.5, 0.5);
    float c = (circle(1.0 - st, 0.5, 0.062) + circle(st, 0.5, 0.062)) * circle(st-0.5, 0.25, 0.25);
    float r = 1. - c;

    c = 1.0 - circle(st-0.5, 0.25, 0.25);
    //r -= c * 0.75;



    return r;
}

float pattern(vec2 st){
    vec2 gdi = gridi(st);
    vec2 gdf = gridf(st);

    return pattern(gdf, (random(gdi) + 1.0) * 128.0 * 128.0 + u_time * 1.2 + random(gdi));
