// factorial
int fact(int x) {
    int prod = 1;
    for (int i = 2; i <= x; i++) {
        prod *= i;
    }
    return prod;
}

// radial term
float r(int m, int n, float rho) {
    if ((n - m) % 2 == 1) return 0.;
    int avg = (n + m) / 2;
    int dev = (n - m) / 2;
    float sum = 0.;
    for (int k = 0; k <= dev; k++) {
        int sgn = 1 - 2 * (k % 2);
        int num = sgn * fact(n - k);
        int den = fact(k) * fact(avg - k) * fact(dev - k);
        int cooef = num / den;
        sum += float(cooef) * pow(rho, float(n - 2 * k));
    }
    return sum;
}

// full zernike polynomial
float zernike(int m, int n, float rho, float theta) {
    float a;
    if (m >= 0) {
        a = cos(float(m) * theta);
    } else {
        m *= -1;
        a = sin(float(m) * theta);
    }
    return r(m, n, rho) * a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // center and normalize coordinates
    vec2 uv = 0.8 * (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // polar decomposition
    float rho = length(uv);
    float theta = atan(uv.y, uv.x);

    // computes the height of each point for a given set of aberrations
    // MAGIC HAPPENS HERE: TRY THESE LINES!
    // float h = zernike(1, 1, rho, theta) + 0.5 * zernike(-1, 1, rho, theta);
    // float h = zernike(0, 2, rho, theta) - 1.6 * zernike(-1, 1, rho, theta);
    float h = zernike(-2, 2, rho, theta) - 0.5 * zernike(0, 4, rho, theta);
    // float h = zernike(1, 5, rho, theta) + 0.5 * zernike(-1, 1, rho, theta);
    // float h = 0.4 * zernike(-2, 4, rho, theta) - 0.8 * zernike(-1, 3, rho, theta) + 0.6 * zernike(2, 2, rho, theta);

    // base color
    vec3 col = mix(vec3(1.), vec3(0., 0., 1.), h) * mix(vec3(1.), vec3(1., 0., 0.), -h);

    // aperture and color normalization
    if (length(uv) > 1.0) {
        col = vec3(0.);
    } else {
        col = clamp(col, vec3(0.), vec3(1.));
    }

    // fringes
    col *= pow(sin(3.14159265359 * (2. * h + 0.5 * cos(iTime))), 2.);

    // output
    fragColor = vec4(col, 1.0);
}

