float sdIsosceles(in vec2 p, in float b, in float h) {
    p.x = abs(p.x);
    float q = clamp((b - p.x + p.y) / (b + h), 0.0, 1.0);
    vec2 re = vec2(p.x - b + b * q, p.y - h * q);
    vec2 be = vec2(p.x - b * min(p.x / b, 1.0), p.y);
    return sqrt(min(dot(re, re), dot(be, be))) * sign(p.x + (p.y - h) * b / h * sign(p.y));
}

float line(in vec2 p, in float h) {
    return length(vec2(p.x, p.y - h * clamp(p.y / h, 0.0, 1.0)));
}
float arrow(in vec2 p) {
    return min(line(p, 0.6)-0.05, 0.25*sdIsosceles((p-vec2(0, 0.6))*4.0, 1.0, 1.5));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragCoord -= iResolution.xy*0.5;
    fragCoord -= viewport*scale;
    #if GRID
    vec2 gridPos = abs((fract((0.5+fragCoord)/(scale)))-0.5);
    float grid = 0.25*max((max(gridPos.x, gridPos.y)*scale - scale*0.5+1.0), 0.0);
    gridPos = abs((fract((0.5+fragCoord)/scale*0.5))-0.5);
    grid += 0.75*max((max(gridPos.x, gridPos.y)*scale*2.0 - scale+1.0), 0.0);
    fragColor.xyz = vec3(grid)*vec3(0.3, 0.3, 1.0);
    float axis = max(max(1.-abs(fragCoord.x+0.5), 1.-abs(fragCoord.y+0.5)), 0.0);
    fragColor = 0.5*mix(fragColor, vec4(1), axis);
    #endif
    vec4 celll = vec4(floor(fragCoord/scale), fract(fragCoord/scale));
    float d = 1000.;
    vec3 color = vec3(0);
    for (int x = -1; x <= 0; x += 1) {
        for (int y = -1; y <= 0; y += 1) {
            vec4 cell = vec4(celll.xy-vec2(x, y), celll.zw+vec2(x, y));
            vec2 v = VectorField(cell.xy);
            if (!(isnan(v.x) || isnan(v.y))) {
                vec2 d = normalize(v);
                float ss = length(v);
                float s = 0.5*(ss+1.0)/ss;
                float dd = arrow((cell.zw*2.0)*mat2(d.y, -d.x,d)*s)*scale/(s*2.0);
                color = mix(color, mix(vec3(0, 1, 0), vec3(1, 0, 0), ss*colorScale), 1.-clamp(dd, 0.0, 1.0));
                d = min(d, dd);
            }
        }
    }
    fragColor.xyz += color;//vec4(1.-clamp(d, 0.0, 1.0));
}

// A recreation of https://lazybrush.dulnan.net/

// Controls:
// - Mouse to draw
// - L: toggle between quadratic bezier curves and line segments
// - S: toggle SDF visualisation
// - P: toggle mouse points

// Settings in Buffer B

// Modified sdBezier() function originally from
// Quadratic Bezier SDF With L2 - Envy24
// https://www.shadertoy.com/view/7sGyWd

#define LINE_WIDTH (iResolution.y * 0.01)
#define POINT_RADIUS (iResolution.y * 0.007)

const int KEY_L = 76;
const int KEY_S = 83;
const int KEY_P = 80;

bool keyToggled(int keyCode) {
    return texelFetch(iChannel1, ivec2(keyCode, 2), 0).r > 0.0;
}

vec4 blendOver(vec4 front, vec4 back) {
    float a = front.a + back.a * (1.0 - front.a);
    return a > 0.0
        ? vec4((front.rgb * front.a + back.rgb * back.a * (1.0 - front.a)) / a , a)
        : vec4(0.0);
}

void blendInto(inout vec4 dst, vec4 src) {
    dst = blendOver(src, dst);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragColor = vec4(1.0);

    float qd = texture(iChannel0, fragCoord / iResolution.xy).x;
    float ld = texture(iChannel0, fragCoord / iResolution.xy).y;
    float pd = texture(iChannel0, fragCoord / iResolution.xy).z;
    float sd = (keyToggled(KEY_L) ? ld : qd) - LINE_WIDTH / 2.0;

    blendInto(fragColor, vec4(0.0, 0.0, 0.0, clamp(0.5 - sd, 0.0, 1.0)));

    if (!keyToggled(KEY_S)) {
        float spacing = iResolution.y * 0.02;
        float thickness = max(iResolution.y * 0.002, 1.0);
        float opacity = clamp(
            0.5 + 0.5 * thickness - 
            abs(mod(sd - (spacing - thickness) * 0.5, spacing) - spacing * 0.5), 
            0.0, 1.0
        ) * 0.5 * exp(-sd / iResolution.y * 8.0);
        blendInto(fragColor, vec4(0.0, 0.0, 0.0, opacity));
    }

    if (keyToggled(KEY_P)) {
        blendInto(fragColor, vec4(1.0, 0.0, 0.0, clamp(POINT_RADIUS - pd + 0.5, 0.0, 1.0)));
    }
}

