// variant of https://shadertoy.com/view/DlcXW7

#define S(v)   smoothstep( 9./R.y, 0., v )                          // AAdraw
#define H(I,t) fract(1e4*sin(dot(I+floor(t),R-17.))) < .5           // random swap ?
#define D(I,t) I == vec2(floor(2.5+t))                              // passing dot ?
#define C(I,t) H(I,t) || D(I,t+.5) || D(I+1.,t+.5)                     // all swap conditions
//#define A(t) clamp( fract(t)*2. -.5, 0., 1. )                     // turn ( 1 = pi/2 )
//#define A(t) smoothstep(0.,1., fract( t )  )                      // versions with easing
  #define A(t) smoothstep(0.,1., fract(t)*1.4 -.2 )

void mainImage( out vec4 O, vec2 u )
{
    float t = iTime, a;
    vec2 R = iResolution.xy,
         V = 5.* u / R.y, U = V + t,
         I = floor(U),                                              // Cell id
         F = fract(U)-.5;                                           // cell local coords
    bool     b =  C(I,t),                                           // swap this cell ? if random or passing dot
         B = b == C(I,t-1.);                                        // same state that previously ?
    a =  b  ? B ? 1. : A( t)                                        // swap :   keep or turn depending on previous state
            : B ? 0. : A(-t);                                       // no swap: keep or turn depending on previous state

    F *= mat2(cos(1.57*a+vec4(0,11,33,0)));                         // rotate cell from prev to new configuration
    O = vec4(  S( abs( min(length(F-.5),length(F+.5)) - .5 ) -.1 )  // draw truchet
             * S(length(F)-.5) );

    O.g += S(length(V-2.5)-.1);                                     // draw green dot
  //O.r += .4*mod(I.x+I.y,2.);                                      // debug: show tiles
  //O.r += .4*float(b != H(I,t));       // debug: tile changed for dot
  //O.b += .5*float(!B);                // debug: tile changed since t-1
}

#define PI 3.1415926535897932384626433832795

#define ringRadius 0.7
#define ringBlur 0.025
#define ringSize 0.05
#define zoom 0.5
#define ringFadeSize 0.025
#define ringFadeStart ringSize - 0.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;

    float angle = PI/4.;
    uv /= zoom;
    uv += iTime*vec2(0.05, 0.07);
    uv *= mat2(
        cos(angle), sin(angle),
        -sin(angle), cos(angle)
    );

    uv += texture(iChannel0, uv).x*0.05;

    vec2 cellPos = floor(uv);
    vec2 odd = mod(cellPos, 2.);
    float checkerboard = mod(cellPos.x + cellPos.y, 2.);

    vec2 cellCoord = fract(mix(uv.xy, uv.yx, checkerboard)*(1. - odd.yx*2.));


    float ring1dist = abs(max(cellCoord.x, cellCoord.y) - ringRadius);
    float ring2dist = abs(1. - min(cellCoord.x, cellCoord.y) - ringRadius);


    float horizontalFade1 = smoothstep(0., ringFadeSize, abs(cellCoord.y - 1. + ringRadius) - ringFadeStart);
    float horizontalFade2 = smoothstep(0., ringFadeSize, abs(cellCoord.y - ringRadius) - ringFadeStart);

    float verticalFade1 = smoothstep(0., ringFadeSize, abs(cellCoord.x + 1. - ringRadius) - ringFadeStart);
    float verticalFade2 = smoothstep(0., ringFadeSize, abs(cellCoord.x - 2. + ringRadius) - ringFadeStart);

    float ring1 = smoothstep(ringBlur, 0., ring1dist - ringSize)*horizontalFade1*verticalFade1;
    float ring2 = smoothstep(ringBlur, 0., ring2dist - ringSize)*horizontalFade2*verticalFade2;


    vec3 col = (ring1 + ring2)*(0.5 + texture(iChannel1, uv).xyz*0.5);


    fragColor = vec4(col,1.0);
}

