    Year of Truchets #028
    05/30/2023  @byt3_m3chanic
    Truchet Core \M/->.<-\M/ 2023 

    dual motion tracks - for cars and stuff.

    @Shane & @iq for compact noise in common tab
*/


#define R           iResolution
#define T           iTime
#define M           iMouse

#define PI         3.14159265359
#define PI2        6.28318530718

const float tc = .2;
const float trs = .45;
const float spd = 1.15;
const float bl = .05;
const vec2 bx = vec2(.45,.075);
const float amt = 6.;

float scale = 5.;

mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a));}
vec3 hue(float a) { return .65+.35*sin(PI*a*vec3(1,.97,.93)*vec3(0.875,0.400,0.149));}
float hash21(vec2 a) { return fract(sin(dot(a, vec2(27.609, 47.583)))*43758.5453);}

//@iq sdf
float box( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b; return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 O, in vec2 F ) {
    if(R.x>1200.) scale = 9.; //up scale on fullscreen

    vec3 C = vec3(.7001);
    vec2 uv = (2.*F-R.xy)/max(R.x,R.y);

    uv *= scale;
    uv *= rot(.06*sin(T*.12));
    uv += vec2(2.33*sin(T*.15),T*.3);

    float px = fwidth(uv.x); 
    vec2 id = floor(uv);
    vec2 q = fract(uv)-.5;

    float hs = hash21(id);
    float ck = mod(id.x+id.y,2.)*2.-1.;
    if(hs>.5) q.x = -q.x;

    vec2 uv2 = vec2(length(q-.5),length(q+.5));
    vec2 p = uv2.x<uv2.y? q-.5 : q+.5;

    // main pattern
    float d = length(p)-.5;
    d = abs(d)-tc;

    // moving truchet parts
    vec2 arc = q-sign(q.x+q.y+.001)*.5;
    float angle = atan(arc.x, arc.y);
    float ud = length(arc);

    float x = fract(amt*ck*angle/PI+T*spd);
    float y = (ud/.5)-.5;
    float z = fract(amt*ck*angle/PI-T*spd);

    vec2 cid = vec2(floor(ud-(.5-tc))/(2.*tc),floor(amt*ck*angle/PI+T*spd));
    vec2 did = vec2(floor(ud-(.5-tc))/(2.*tc),floor(amt*ck*angle/PI-T*spd));
    cid.y = mod(cid.y,amt);
    did.y = mod(did.y,amt);

    vec3 K = hue((cid.y*.2)+75.);
    vec3 R = hue((did.y*.2)+125.);
    if(mod(cid.y,4.)==0.) K=vec3(.6);
    if(mod(did.y,3.)!=0.) R=vec3(.6);

    if(hs<.5 ^^ ck>0.) y=1.-y;

    vec2 tuv = vec2(x,y);
    float k = box(tuv+vec2(0,.1)-.5, bx)-.012;
    float k2 = box(vec2(z,y-.1)-.5, bx)-.012;

    // gnd
    float sc = 6.;

    vec2 iv = floor(uv*sc)+id;
    vec2 vv = fract(uv*sc)-.5;
    float xs = fbm(vec3(iv.x,0,iv.y)*.035);
    xs = smoothstep(.5,.95,xs);
    float cy = length(vv)-(xs*.55);

    if (xs>.1) {
        float fx = fwidth(uv.x*sc); 
        C = mix(C,vec3(clamp(.7-xs,.2,1.)),smoothstep(fx,-fx,cy));
    } 

    C = mix(C,C*.65,smoothstep(.05+px,-px,d-.015));

    C = mix(C,vec3(.4),smoothstep(px,-px,abs(d)-.025));
    C = mix(C,vec3(.7001),smoothstep(px,-px,d));

    C = mix(C,K,smoothstep(px,-px,k));
    C = mix(C,R,smoothstep(px,-px,k2));

    C = pow(C,vec3(.4545));
    O = vec4(C,1.);
}

