        //uv = vec2(cos(.2*(uv.x-uv.y)), sin(.3*(uv.x-uv.y)));

    }

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));
    return c;

}


vec3 warp(in vec2 uv) {
    vec3 c = pools(uv);

    int iter = 0;
    float amp = 334.9;
    float f = .00112;

    float a = 1.; 
    for (int i = 0; i < iter; i++) {
       a *= amp;
    }

    for (int i = 0; i < iter; i++) {
        a /= amp;
        f *= 1.2;
        c = a*pools(uv + f*vec2(c.r-c.g, c.g - c.b)) ;
    }
    return c;
}


