// Graphing with approximated constant thickness, following this article:
//
// https://iquilezles.org/articles/distance/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    float w = 1.0/iResolution.y; // size of a pixel
    float x = fragCoord.x * w;
    float y = fragCoord.y * w;

    // function f(x) and naive distance
    float f = 0.5 + 0.005/x + 0.01*x/(x-1.0) + 0.1*sin(x*20.0);
    float d = abs(f-y);

    // enable and disable the technique
    if( mod(iTime,2.0)>1.0 )
    {
        // derivative f'(x) and better distance estimation
        float fy = -0.005/(x*x) - 0.01/((x-1.0)*(x-1.0)) + 0.1*20.0*cos(20.0*x);
        fy = min( abs(fy), 40.0 );
        // distance estimation
        d /= sqrt( 1.0 + fy*fy );
    }
    else
    {
        d *= 0.707107;
    }

    // background
    vec3 col = vec3(0.15);

    // graph
    float thickness = iResolution.y/135.0;
    // graph thickness is 8 pixels at 1080p
    // cubic filtering is 2 pixels wide
    col = mix( col, vec3(0.0,0.7,1.0), smoothstep((0.5*thickness+2.0)*w,
                                                  (0.5*thickness+0.0)*w,
                                                   d) );
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Dimensiunile ecranului
    vec2 resolution = iResolution.xy;

    // Normalizarea coordonatelor
    vec2 uv = ( fragCoord - resolution) / resolution.y;

    // Redimensionarea la aspect ratio
    uv.x *= resolution.x / resolution.y;

    // Calculul coordonatelor 3D
    float x = uv.x;
    float y = uv.y;
    float z = sin(uv.x * 5.0) * cos(uv.y * 5.0) *5.0 ;

    // Calculul distanei fa de centrul 
    float distance = sqrt(x * x + y * y + z * z);

    // Calculul valorii fractalului n funcie de distan
    float l = clamp(uv.x,uv.y,0.0);
    //float fractalValue = z + sin(distance * distance * cos(iTime));
    float fractalValue = z*z*z*sin(distance *exp(l)/ (z*z * cos(iTime) * distance+sin(iTime) ));

    // Setarea culorii n funcie de fractalValue
    vec3 color = vec3(sin(fractalValue)/fractalValue);

    // Afisarea culorii
    fragColor = vec4(fractalValue+sin(iTime)/color.x,color.y/fractalValue-cos(iTime),fractalValue-sin(iTime)/color.z, 1.0);
}

/**
