#define halfpi acos(0.0)
#define pi (acos(0.0)*2.0)
#define tau (acos(0.0)*4.0)
#define constant (180.0/pi)
#define PHI ((sqrt(5.0)+1.0)/2.0)

struct Ray
{
    vec3 origin;
    vec3 dir;
};

struct HitInfo
{
    vec3 color;
    vec3 normal;
    float roughness;
    float t;
    bool hit;
};

struct Sphere
{
    vec3 ce;
    float ra;
    vec3 color;
    float roughness;
};

Ray cam(vec3 pos, vec2 turn, float zoom, vec2 uv)
{
    vec3 dir = vec3(sin(turn.x)*cos(turn.y), cos(turn.x)*cos(turn.y), sin(turn.y));

    vec3 up = vec3(sin(turn.x)*cos(turn.y+halfpi), cos(turn.x)*cos(turn.y+halfpi), sin(turn.y+halfpi));

    vec3 right = vec3(sin(turn.x+halfpi), cos(turn.x+halfpi), 0);

    vec3 p = (dir*zoom)+(up*uv.y)+(right*uv.x);

    return Ray(pos, normalize(p));
}

HitInfo sphere( Ray ray, Sphere sphere)

{

    vec3 oc = ray.origin - sphere.ce;

    float b = dot( oc, ray.dir );

    float c = dot( oc, oc ) - sphere.ra*sphere.ra;

    float h = b*b - c;

    bool hit = true;

    if( h<0.0 ) {hit = false; h = 0.0;} // no intersection

    h = sqrt( h );

    float t = -b-h;
    if(t<=0.0)
    {
        hit=false;
    }
    vec3 hitPos = ray.origin+(ray.dir*t);
    vec3 normal = normalize(hitPos-sphere.ce);

    return HitInfo(sphere.color, normal, sphere.roughness, t, hit);

}


vec3 hemisphere(vec3 normal, vec2 uv, float seed)
{
    int points = 100000;
    int i = int(rand(vec3(uv.x, uv.y, seed))*float(points));
    float dist = float(i)/float(points);
    float angle = float(i)*pi*PHI;
    vec3 p = vec3(sin(acos(1.0-(2.0*dist)))*cos(angle), sin(acos(1.0-(2.0*dist)))*sin(angle), 1.0-(2.0*dist));
    //if(dot(p, normal)<0.0){p = -p;}
    return normalize(p+normal);
}



HitInfo ClosestCollision(Ray ray)
{
    const int sphereNum = 3;
    float speed = 0.1;
    Sphere spheres[sphereNum] = Sphere[sphereNum](Sphere(vec3(-cos(iTime*speed), -sin(iTime*speed), 0), 1.0, vec3(1, 0.3, 0.3), 1.0), Sphere(vec3(cos(iTime*speed), sin(iTime*speed), 0), 1.0, vec3(0.3, 1.0, 0.3), 1.0), Sphere(vec3(0, 0, sqrt(3.0)), 1.0, vec3(1.0, 1.0, 1.0), 0.0));

    HitInfo closestHit;
    closestHit.t = 999999999999.0;

    for(int i = 0; i<sphereNum; i++)
    {
        Sphere s = spheres[i];
        HitInfo hitInfo = sphere(ray, s);

        if(hitInfo.hit && hitInfo.t<closestHit.t)
        {
            closestHit = hitInfo;
        }
    }

    return closestHit;
}

vec3 sky(Ray ray, vec2 dir, vec3 col, float brightness, float angle)
{
    dir/=constant;
    //return col*clamp(float(dot(vec3(sin(dir.x)*cos(dir.y), cos(dir.x)*cos(dir.y), sin(dir.y)), ray.dir)>angle)*dot(vec3(sin(dir.x)*cos(dir.y), cos(dir.x)*cos(dir.y), sin(dir.y)), ray.dir), 0.0, 1.0)*brightness;
    vec4 o = texture(iChannel1, vec3(ray.dir.y, ray.dir.z, ray.dir.x));
    return (o.xyz*o.xyz*o.xyz)*brightness*col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int samplesPerFrame = int(1.0/iTimeDelta);
    vec3 totalCol = vec3(0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvn = fragCoord/iResolution.xy;
    vec2 uv = uvn-0.5;
    uv.x*=iResolution.x/iResolution.y;

    for(int sampl = 0; sampl<samplesPerFrame; sampl++)
    {

        Ray ray = cam(vec3(sin(-iTime)*-6.0, cos(-iTime)*-6.0, 1), vec2(-iTime*constant, 0)/constant, 1.0, uv+((vec2( rand(uv.x+float(sampl*2)), rand(uv.y-float(sampl*5)) )-0.5)/iResolution.xy) );

        vec3 col = vec3(1, 1, 1);
        float brightness = 1.0;

        int bounces = 8;

        for(int i = 0; i<bounces; i++)
        {
            HitInfo hitInfo = ClosestCollision(ray);



            if(hitInfo.hit)
            {
                col *= hitInfo.color*brightness;
                if(i==bounces-1){col*=0.0;}
            }
            else
            {
                col *= sky(ray, vec2(iTime*halfpi*halfpi, iTime*5.0), vec3(1,1,1), 4.0, 0.0);
                col *= brightness;
                break;
            }
            Ray reflection = Ray(ray.origin+(ray.dir*hitInfo.t), (hemisphere(hitInfo.normal, fragCoord.xy/29.54, fract(float(iFrame*(sampl+1))/(6.0*pi*PHI))+1.0)*hitInfo.roughness) + (reflect(ray.dir, hitInfo.normal)*(1.0-hitInfo.roughness)));
            ray = reflection;
            //brightness = clamp((dot(hitInfo.normal, reflection.dir)*(hitInfo.roughness)) + (1.0-hitInfo.roughness), 0.0, 1.0);
            brightness = 1.0;
        }
        totalCol += col;
    }
    // Output to screen
    fragColor = vec4((totalCol/float(samplesPerFrame)),1.0);//+texture(iChannel0,  uvn);
}

