// time
float t;

// Motivation:
// I wanted to make an interactive kinematics placement program so you
// could make something like this by hand:
// https://www.shadertoy.com/view/cltXzN
// More complicated objects require chains of references to other objects,
// so I've given up on it for now

void drawObj(inout vec3 col, vec2 uv, float i) {
    vec4 obj = cell(ch0, vec2(i, 0));
    vec2 p = (obj.xy - 0.5 * res) / res.y;
    if (obj.w == 1.) { // Point (x, y, 0, 1)
        float d = length(uv - p);
        float s = ss(1., thick[1] - d);
        col = mix(col, vec3(1), s);
    } else if (obj.w == 2.) { // Line (start, end, 0, 2)
        vec2 start = getPosition(obj.x);
        vec2 end   = getPosition(obj.y);
        start = (start.xy - 0.5 * res) / res.y;
        end   = (end.xy   - 0.5 * res) / res.y;
        vec2 dir = 1.5 * thick[1] * normalize(end - start);
        float d = seg(uv, start + dir, end - dir);
        float s = ss(1., thick[2] - d);
        col = mix(col, vec3(1, 0, 0), s);
    } else if (obj.w == 3.) { // Orbit point (start, angle, radius, 3)
        vec2 start = cell(ch0, vec2(obj.x, 0) + 0.5).xy;
        vec2 p = start + Dir(obj.y + t) * obj.z;
        p = (p - 0.5 * res) / res.y;
        float d = length(uv - p);
        float s = ss(1., thick[1] - d);
        col = mix(col, vec3(1, 1, 0), s);
    } else if (obj.w == 4.) { // Orbit point (reverse) 
        vec2 start = cell(ch0, vec2(obj.x, 0) + 0.5).xy;
        vec2 p = start + Dir(obj.y - t) * obj.z;
        p = (p - 0.5 * res) / res.y;
        float d = length(uv - p);
        float s = ss(1., thick[1] - d);
        col = mix(col, vec3(1, 0, 1), s);
    }
}

void drawObjIndicator(inout vec3 col, vec2 uv, float objType) {
    float r = 0.1;
    uv -= (0.5 * res / res.y - r - 0.03) * vec2(-1, 1);

    float opacity = 0.9;

    // Draw box
    float dBox = box(uv, vec2(r));
    float sBox = ss(1., 0.01 - abs(0.01 - dBox));
    col = mix(col, vec3(1), 0.5 * sBox);

    // Draw object indicator
    if (objType == 0.) { // Grab
        vec2 v = vec2(-1, 1) * 0.032;
        float d1 = seg(uv, v.xx, v.yy);
        float d2 = seg(uv, v.xy, v.yx);
        float s = ss(1., 0.01 - min(d1, d2));
        col = mix(col, vec3(0.8), opacity * s);
    } else if (objType == 1.) { // Point
        float d = length(uv);
        float s = ss(1., 0.02 - d);
        col = mix(col, vec3(1), opacity * s);
    } else if (objType == 2.) { // Line
        float d = seg(uv, vec2(-0.06), vec2(0.06));
        float s = ss(1., 0.008 - d);
        col = mix(col, vec3(1, 0, 0), opacity * s);
    } else if (objType == 3.) { // Orbit point
        vec2 p = 0.06 * Dir(3. * iTime);
        float d = length(uv);
        float dp = length(uv - p);
        float s = ss(1., 0.005 - abs(0.04 - d));
        float sp = ss(1., 0.01 - dp);
        col = mix(col, vec3(1), opacity * s);
        col = mix(col, vec3(1, 1, 0), opacity * sp);
    } else if (objType == 4.) { // Orbit point (reverse)
        vec2 p = 0.06 * Dir(-3. * iTime);
        float d = length(uv);
        float dp = length(uv - p);
        float s = ss(1., 0.005 - abs(0.04 - d));
        float sp = ss(1., 0.01 - dp);
        col = mix(col, vec3(1), opacity * s);
        col = mix(col, vec3(1, 0, 1), opacity * sp);
    }
}

void drawCursor(inout vec3 col, vec2 uv, vec2 ms) {
    float d = abs(0.015 - length(uv - ms));
    float s  = ss(1., 0.009 - d);
    float s2 = ss(1., 0.003 - d);
    vec3 colCursor = mix(vec3(s), vec3(0), s2);
    col = mix(col, colCursor, 0.5 * s);
}

void mainImage( out vec4 O, in vec2 px )
{
    vec2 uv = (px - 0.5 * res) / res.y;
    vec2 ms = (iMouse.xy - 0.5 * res) / res.y;

    float lastIndex = cell(ch0, pxIndex).r;  
    float objType = cell(ch0,pxObjType).x;

    t = spd * iTime;   
    // Pause time when placing non-lines
    if (iMouse.w <= 0. && iMouse.z > 0. && objType != 2.)
        t = 0.;

    vec3 col = vec3(0);

    // Draw line from mouse to closest object
    float index = getClosestIndex(iMouse.xy, 100.);
    vec2 q = getPosition(index);
    q = (q - 0.5 * res) / res.y;
    float d = seg(uv, ms, q);
    float s = ss(1., 0.005 - d);
    col = mix(col, vec3(1), 0.35 * s);

    // Draw objects
    for (float i = 0.; i < lastIndex; i++) 
        drawObj(col, uv, i);

    // Highlight objects when mouse is close to them
    /*
    for (float i = 0.; i < lastIndex; i++) {
        vec2 p = (obj.xy - 0.5 * res) / res.y;
        float d = length(uv - p);
        float dMs = length(ms - p);
        if (dMs <= thick[1]) {
            float s = ss(1., thick[1] - 0.008 - d);
            col = mix(col, vec3(0), s);
        }
    } //*/

    drawObjIndicator(col, uv, objType);
    drawCursor(col, uv, ms);

    O = vec4(col, 1);
}

