// SDFs
float box(in vec2 p, in vec2 b) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

// Object code (using macros because Common tab hates everything)
#define getPosition(index) getPosition0(ch0, index, spd * t)
vec2 getPosition0(sampler2D ch, float index, float time) {
    vec4 obj = cell(ch, vec2(index, 0));
    if (obj.w == 1.) // Point
        return obj.xy;
    if (obj.w == 3.) { // Orbit point
        vec2 start = cell(ch, vec2(obj.x, 0)).xy;
        return start + Dir(obj.y + time) * obj.z;
    }
    if (obj.w == 4.) { // Orbit point (reverse)
        vec2 start = cell(ch, vec2(obj.x, 0)).xy;
        return start + Dir(obj.y - time) * obj.z;
    }
    return vec2(-1);
}

#define getClosestIndex(p, lastIndex) getClosestIndex0(ch0, res.x, t, p, lastIndex)
float getClosestIndex0(sampler2D ch, float resx, float time, vec2 p, float lastIndex) {
    float d = 4. * resx * resx;
    float objIndex = -1.;
    for (float i = 0.; i < lastIndex; i++) {
        vec2 q = getPosition0(ch, i, time);
        if (q == vec2(-1)) 
            continue;

        float d0 = dot2(p - q);
        if (d0 < d) {
            d = d0;
            objIndex = i;
        }
    }
    return objIndex;
}

// Hash
float h11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

