        vec4 obj = cell(ch0, vec2(i, 0));
        vec2 q = getPosition(i);
        if (obj.w == 2.) {
            vec2 start = getPosition(obj.x);
            vec2 end = getPosition(obj.y);
            //vec2 q = 0.5 * (start + end);
            float d0 = seg(ms, start, end);
            if (d0 < d) {
                d = d0;
                objIndex = i;
            }          
        }
    }
    return objIndex;
}
//*/

void setGrabObject(inout vec4 O, vec2 px, vec2 ms, float index) {
    if (px == pxGrab) {
        float objIndex = getClosestIndex(ms, index);
        vec2 objPos = getPosition(objIndex).xy;
        if (objPos != vec2(-1)) {
            vec2 objUv = (objPos - 0.5 * res) / res.y;
            vec2 msUv = (ms - 0.5 * res) / res.y;
            // if mouse is close to object, grab it
            if (dot2(objUv - msUv) < 6. * thick[1] * thick[1])
                O.x = objIndex;
        }
    }
}
// This code is very similar to objection creation code.
// Could reuse it
void moveGrabObject(inout vec4 O, vec2 px, vec2 ms, float grabIndex) {
    if (px == vec2(grabIndex, 0) + 0.5) {
      vec4 obj = cell(ch0, vec2(grabIndex, 0));
      if (obj.w == 1.) // Point
          O.xy = ms;
      else if (obj.w == 3. || obj.w == 4.) { // Orbit points
          vec4 startObj = cell(ch0, vec2(obj.x, 0));
          vec2 dir = ms - startObj.xy;
          float angle = atan(dir.y, dir.x);
          float radius = length(dir);
          O.yz = vec2(angle, radius);
      }
    }
}
void releaseGrabObject(inout vec4 O, vec2 px) {
    if (px == pxGrab) 
        O.x = -1.;
}

void mainImage( out vec4 O, in vec2 px )
{
    vec2 ms = iMouse.xy;

    float lastIndex    = cell(ch0, pxIndex).x;
    float objType      = cell(ch0, pxObjType).x;
    float grabIndex    = cell(ch0, pxGrab).x;
    float wasMouseDown = cell(ch0, pxMouseDown).x;

    t = iTime;
    if (iMouse.w <= 0. && iMouse.z > 0. && objType != 2.)
        t = 0.;

    // Read buffer, and initialize buffer on 1st frame
    O = (iFrame > 1) ? cell(ch0, ivec2(px)) : vec4(0);

    // Toggle object type
    if (px == pxObjType) {
        if (key_pressed(KEY_E)) 
            O.x = mod(O.x + 1., 5.);
        else if (key_pressed(KEY_Q))
            O.x = mod(O.x - 1., 5.);
    }

    // Delete last object
    if (key_pressed(KEY_Z)) {
        if (px == pxIndex)
            O.x = max(0., O.x - 1.);
        else if (px == vec2(lastIndex, 0) + 0.5)
            O = vec4(0);
    }

    // Was mouse down last frame
    if (px == pxMouseDown) 
        O.x = (iMouse.z > 0.) ? 1. : 0.;

    // Reset
    // if (key_pressed(KEY_R)) 
    //     O = vec4(0);


    // -- Mouse interaction and object placement --

    // Grab
    if (objType == 0.) { 
        if (iMouse.w > 0.)           setGrabObject(O, px, ms, lastIndex);
        else if (iMouse.z > 0.)      moveGrabObject(O, px, ms, grabIndex);
        else if (wasMouseDown == 1.) releaseGrabObject(O, px);
    } 
    // Point (x, y, 0, objType)
    else if (objType == 1.) {
        if (iMouse.w > 0.) {
            if (px == pxIndex) 
                O.x = lastIndex + 1.;
            else if (px == vec2(lastIndex, 0) + 0.5)
                O = vec4(ms, 0, objType);    
        } 
    } 
    // Line (startIndex, endIndex, 0, objType)
    else if (objType == 2.) { 
        if (iMouse.w > 0.) {
            float startIndex = getClosestIndex(ms, lastIndex);
            if (startIndex != -1.) {
                if (px == pxIndex) 
                    O.x = lastIndex + 1.;
                else if (px == vec2(lastIndex, 0) + 0.5)
                    O = vec4(startIndex, startIndex, 0, objType);
            }
        } else if (iMouse.z > 0.) {
            float endIndex = getClosestIndex(ms, lastIndex);
            if (endIndex != -1. && px == vec2(lastIndex - 1., 0) + 0.5)
                 O.y = endIndex;
        }     
    } 
    // Orbit point (startIndex, 0, 0, objType)
    else if (objType == 3. || objType == 4.) { 
        if (iMouse.w > 0.) {
            float startIndex = getClosestIndex(ms, lastIndex);
            if (startIndex != -1.) {
                vec4 startObj = cell(ch0, vec2(startIndex, 0));
                float angle = 0.;
                float radius = length(ms - startObj.xy);
                if (px == pxIndex) 
                    O.x = lastIndex + 1.;
                else if (px == vec2(lastIndex, 0) + 0.5)
                    O = vec4(startIndex, angle, radius, objType);
            }
        } else if (iMouse.z > 0.) {
            vec4 orbitObj = cell(ch0, vec2(lastIndex - 1., 0));
            vec4 startObj = cell(ch0, vec2(orbitObj.x, 0));
            vec2 dir = ms - startObj.xy;
            float angle = atan(dir.y, dir.x);
            float radius = length(dir);
            if (px == vec2(lastIndex - 1., 0) + 0.5)
                O.yz = vec2(angle, radius);
        }
    } 

}

