NewHit newInt(vec3 ro,vec3 rd,Obj objs[objC]){
    NewHit h;
    h.len = -1.;

    for(int i = 0; i < objC; i++) {
        Obj o = objs[i];
        //if(!aabbInt(ro,rd,o.bound)) continue;
        if(o.sph.isActive) {IntRes res = newSphereInt(ro,rd,o.sph);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.box.isActive) {IntRes res = newBoxInt(ro,rd,o.box);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.quad.isActive) {float res = newQuadInt(ro,rd,o.quad);if(res>=.0001&&(res<h.len||h.len<0.)){h.o=o;h.ff=true;h.len=res;}}
    }

    Obj o = h.o;
    vec3 p = ro+rd*h.len;

    if(o.sph.isActive) h.nor = normalize(p-o.sph.c);//*sign(o.sph.rad);
    else if(o.box.isActive) {BoxBody b = o.box;mat3 r = rot3(b.rot);mat3 rn = rot3(-b.rot);h.nor = newBoxNor(b.pos+(p-b.pos)*rn,b)*r;}
    else if(o.quad.isActive) {QuadBody q = o.quad;h.nor = normalize(cross(q.v1-q.v0,q.v2-q.v0))*(q.fn?-1.:1.);}

    //h.nor *= h.ff?1.:-1.;

    return h;
}

const vec3 sunDir = normalize(vec3(.5,1.,.75));
//Sebastian Lagues's sky function
vec3 getSky(float t,vec3 rd) {
    #if SkyMode == 1
    float skyGradT = pow(smoothstep(0.,.4,rd.y),.35);
    vec3 skyGrad = mix(vec3(.05),vec3(.3,.6,.9),skyGradT);

    vec3 sun = pow(max(0.,dot(rd,sunDir)),40.)*10.*vec3(1.,.85,.7);

    float groundT = smoothstep(-.01,0.,rd.y);

    return mix(vec3(.25),skyGrad,groundT)+sun;
    #elif SkyMode == 0
    return vec3(0.);
    #elif SkyMode == 2
    return SRGBToLinear(texture(iChannel1,rd).xyz);
    #endif
}

vec3 newRay(inout vec3 ro,inout vec3 L,vec3 P,in vec3 N,Mat m,bool ff) {
    vec3 oro = ro, V = -L;
    ro = P;

    float T = length(oro-P);
    float scatterProb = 1.-exp(-T*(ff?airDens:m.scatDens));
    if(scatterProb>rand()){
        P = oro-V*rand()*T;
        N = hemi(V);
        L = reflect(-V,N);
        return m.col;
    }

    float F = pow(1.-md(-V,sign(dot(-V,N))*N),5.);
    float r = m.r+(1.-m.r)*F;
    vec3 H = importanceGGX(r,N);
    if(m.m>rand()){ // Metal (Glossy Reflection)
        L = reflect(-V,mix(H,N,round(rand())));
        return m.col;
    } else { // Dielectric (Refraction)
        float eta = ff?1./m.IOR:m.IOR;
        L = refract(-V,H,eta);
        if(length(L)==0.) L = reflect(-V,H);
        return m.col;
    }
}

