// Under the assumption that the SDF in the neighbourhood of the pixel
// is continuous and linear (true for almost all SDFs), this shader
// shows how to calculate the exact portion of a pixel covered by an
// SDF, useful for anti-aliasing.

// The SDF versions of these functions here assume that the provided
// signed-distance values are in screen-space (i.e. 1 unit of distance
// corresponds to the width of a pixel). The shader also provides the
// aastep(edge, x) function that works for non-SDF values as a drop-in
// replacement for step(edge, x) but with anti-aliased output in [0, 1].

// These functions are pretty overkill for anti-aliasing though. The
// difference between these functions and the regular smoothstep/fwidth/
// dFdx/dFdy trick is only noticable if you do some hard pixel peeping.
// I mainly made this just for peace of mind knowing that there is a
// "correct" way to do anti-aliasing for SDFs.

float areaSquareLine(vec2 n, float d) {
    // https://www.desmos.com/calculator/dorvdj5nbq for visualization
    n = abs(n);
    n = vec2(max(n.x, n.y), min(n.x, n.y));
    float a = abs(d);
    float b;

    if (n.y != 0.0 && n.x - n.y <= 2.0 * a) {
        vec2 c = 1.0 + (n - 2.0 * a) / n.yx;
        b = 1.0 - max(c.x, 0.0) * c.y / 8.0;
    } else {
        b = min(0.5 + a / n.x, 1.0);
    }

    return d < 0.0 ? 1.0 - b : b;
}

float areaSquareLineAverage(float d) {
    // Approximate average value of areaSquareLine(n, d) w.r.t. n
    float a = abs(d);
    float b;

    if (a < 1.0 / sqrt(2.0)) {
        b = 4.1434218 * pow(a, 12.647891) -
            1.3070394 * pow(a, 3.9787831) +
            1.0998631 * a +
            0.5012205;
        b = clamp(b, 0.0, 1.0);
    } else {
        b = 1.0;
    }

    return d < 0.0 ? 1.0 - b : b;
}

float sdfFill(float sd) {
    // Return the fraction of the pixel covered by the SDF (i.e. where
    // sd < 0.0)
    vec2 n = vec2(dFdx(sd), dFdy(sd));

    if (n == vec2(0.0)) {
        // Ambiguous case, so instead we just return the average. This
        // is the problem with using dFdx/dFdy that would occur much
        // less frequently if we had access to the analytic derivatives.
        return areaSquareLineAverage(-sd);
    }

    n = normalize(n);
    return areaSquareLine(n, -sd);
}

float sdfOutline(float sd, float width) {
    // Return the fraction of the pixel covered by the outline of the
    // SDF (i.e. where abs(sd) < width / 2.0)
    return sdfFill(sd - width / 2.0) - sdfFill(sd + width / 2.0);
}

float aastep(float edge, float x) {
    // Return the fraction of the pixel where step(edge, x) == 1.0 by
    // approximating the distance to the edge with derivatives.
    x -= edge;
    vec2 n = vec2(dFdx(x), dFdy(x));

    if (n == vec2(0.0)) {
        // Ambiguous case, so we resort to using regular step(edge, x).
        // We can't use areaSquareLineAverage(x) because that function
        // assumes that x measures distance across pixels but we have no
        // information about distance here as the derivatives are zero.
        return step(edge, x);
    }

    float l = length(n);
    return areaSquareLine(n / l, x / l);
}


//====================================================================//


float sdCircle(vec2 p, vec2 c, float r) {
    return distance(p, c) - r;
}

float sdSegment(vec2 p, vec2 a, vec2 b) {
    // https://www.shadertoy.com/view/3tdSDj
    vec2 ap = p - a;
    vec2 ab = b - a;
    return distance(ap, ab * clamp(dot(ap, ab) / dot(ab, ab), 0.0, 1.0));
}

float sdStar(vec2 p, float r, float rf) {
    // https://www.shadertoy.com/view/3tSGDy
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x, k1.y);
    p.x = abs(p.x);
    p -= 2.0 * max(dot(k1, p), 0.0) * k1;
    p -= 2.0 * max(dot(k2, p), 0.0) * k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf * vec2(-k1.y, k1.x) - vec2(0.0, 1.0);
    float h = clamp(dot(p, ba) / dot(ba, ba), 0.0, r);
    return length(p - ba * h) * sign(p.y * ba.x - p.x * ba.y);
}

vec4 blendOver(vec4 front, vec4 back) {
    float a = front.a + back.a * (1.0 - front.a);
    return a > 0.0
        ? vec4((front.rgb * front.a + back.rgb * back.a * (1.0 - front.a)) / a , a)
        : vec4(0.0);
}

void blendInto(inout vec4 dst, vec4 src) {
    dst = blendOver(src, dst);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec4 color = vec4(0.918, 0.894, 0.843, 1.0);
    vec2 p = fragCoord;
    vec2 s = iResolution.xy;
    float h = iResolution.y;

    { // Circle
        vec2 c = (vec2(cos(iTime), cos(iTime + 2.3)) * 0.4 + 0.5) * s;
        float r = (cos(iTime * 2.0 + 4.5) * 0.3 + 0.5) * h * 0.3;
        float sd = sdCircle(p, c, r);
        blendInto(color, vec4(0.471, 0.549, 0.471, sdfFill(sd)));
        blendInto(color, vec4(0.171, 0.249, 0.171, sdfOutline(sd, h * 0.02)));
    }

    { // Line
        vec2 a = abs(iMouse.zw) + 0.5;
        vec2 b = iMouse.xy + 0.5;
        float sd = sdSegment(p, a, b) - h * 0.05;
        blendInto(color, vec4(0.549, 0.471, 0.471, sdfFill(sd)));
        blendInto(color, vec4(0.249, 0.171, 0.171, sdfOutline(sd, h * 0.01)));
    }

    { // Star
        vec2 c = (vec2(cos(iTime + 1.0), cos(iTime * 0.4 + 2.3)) * 0.5 + 0.5) * s;
        float r = (cos(iTime * 1.5 + 2.5) * 0.3 + 0.4) * h * 0.4;
        float sd = sdStar(p - c, r, 0.5);
        blendInto(color, vec4(0.471, 0.471, 0.549, sdfFill(sd)));
        blendInto(color, vec4(0.171, 0.171, 0.349, sdfOutline(sd, h * 0.03)));
    }

    fragColor = vec4(pow(color.rgb * color.a, vec3(1.0 / 2.2)), 1.0);
}

