float integrate(float x, float y, float equation_output)
{
    if (y > 0.0 && y < equation_output)
    {
        return 1.0;
    }
    else
    {
        if (y < 0.0 && y > equation_output)
        {
            return -1.0;
        }
        else
        {
        return 0.0;
        }
    }
}

float integral = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord/iResolution.xy -0.5 ) * 2.0;
    //scale
    float scale = 10.0;
    uv *= scale;


    //create line value where the graph evaluates
    float line = 0.0;

    //this evaluates to the value of the equation, write the equation here
    float equation = sin(uv.x);

    //thickness of the line
    float thickness = 0.01;
    thickness *= scale;
    //graphing calculator
    if ( uv.y >= equation - thickness/2.0 && uv.y <= equation + thickness/2.0 )
    {
        line = 1.0;
    }

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float spot_integral = 0.0;
    //integrate
    spot_integral = integrate(uv.x, uv.y, equation);
    integral += spot_integral;

    //combine shader with line
    col *= (line + abs(spot_integral));

    // Output to screen
    fragColor = vec4(col,1.0);
}

//creates a color pallette mask
vec3 pallette( float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 1.0, 0.557);

    return a + b * cos( 5.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //normalize coordinates to a unit grid (x,y) -> (u,v)
    //iResolution gives dimensions of canvas, is a vec3, .xy only uses its first two values
    //iresolution.xy swizzles the attributes of the irezolution vector into a new vector. the object.attributeatribute syntax is a universal thing
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0;


    //original coordinates
    vec2 uv0 = uv;

    //shrinks and duplicates the image
    //uv = fract(uv * 2.0) -0.5;

    //corrects for nonsquare frames
    uv.x *= iResolution.x / iResolution.y;

    //float fot the distance magnitude from coordinates to origin
    float d = length(uv);

    //mask color variable
    vec3 col = pallette(d + iTime/10.0);


    //this should adjust the length harmonically based on the theta of the point
    d += cos( sin( uv.y/d * iTime/10.0 ) ) ;

    //same as below but executed better, it makes a circle
    //by using sin we can make many circles
    d = sin(d * 20.0 + iTime);
    d = abs(d);
    //d = smoothstep(0.0, 1.0, d);


    d = 0.2/d;

    //makes a circular region where d1 = 1
    float d1 = 0.0;
    if (d <= 1.0 && d >= 0.4)
    {
        d1 = 1.0;
    }

    //applys color mask to d
    col *= d;

    //outputs a color gradient, inputting values of uv for the color values 
    fragColor = vec4(col, 1.0);

}

