float ghs,shs,sd,gd,tspeed=0.,tmod=0.,ga1=0.,ga2=0.,ga3=0.,ga4=0.,gtk,stk;

// constants
const float size = 1.15;
const float hlf = size/2.;
const float db = size*5.;

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 p){return fract(sin(dot(p, vec2(27.609,47.983)+iDate.z))*43758.5453);}
float lsp(float b, float e, float t){return clamp((t-b)/(e-b),0.,1.); }
float eoc(float t){return (t = t-1.)*t*t+1.; }

//@iq sdf & extrude
float cap( vec3 p, float h, float r ){
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float opx(in float sdf, in float pz, in float h){
    vec2 w = vec2( sdf, abs(pz) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));
}

vec2 map(vec3 pos){
    vec2 res = vec2(1e5,0);

    vec2 uv = pos.xz+vec2(ga1,ga2);
    vec2 r = (uv*size)-.5;
    vec2 ir = floor(r);

    vec2 id = floor(uv*size);
    float rnd = hash21(id);
    float xnd = fract(rnd*32.323);

    r = fract(r)-.5;
    vec2 q = fract(uv*size)-.5;
    if (rnd>.5) q.x=-q.x; 

    vec2 cv = vec2(length(q-.5),length(q+.5));
    vec2 p = cv.x<cv.y?q-.5:q+.5;

    float thc = .05;
    float k = length(p)-.5;k = abs(k)-thc;

    float sw = .1+.095*sin(pos.x*1.95+ga4);
          sw-= .1+.095*cos(pos.z*2.25+ga3);
    ghs = sw;

    if(xnd>.75) k = min(length(q.x)-thc,length(q.y)-thc);
    gd = k;

    float d = opx(k-.07,pos.y,.25)-.05;
    float d3= opx(abs(k)-.03,pos.y+sw-.225,.015)-.005;

    if(d3<res.x) {
        res = vec2(d3,2.);
        hit=vec3(q.x,pos.y,q.y);
    }

    float hs = hash21(ir);
    float b = cap(vec3(r.x,pos.y,r.y),.45,.15);
    if(b<res.x&&hs>.6) {
        res = vec2(b,4.);
        hit=pos+vec3(ga1,0,ga2);
    }

    float gnd = pos.y+.01;
    float gnt = d-.001;
    gnd = max(gnd,-gnt);

    if(gnd<res.x) {
        res = vec2(gnd,gnt<gnd?3.:1.);
        hit=pos+vec3(ga1,0,ga2);
    }

    return res;
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec2 marcher(vec3 ro, vec3 rd, int steps) {
    float d = 0., m = 0.;
    for(int i=0;i<steps;i++){
        vec2 ray = map(ro + rd * d);
        if(ray.x<MIN_DIST*d||d>MAX_DIST) break;
        d += i<32?ray.x*.35:ray.x*.85;
        m  = ray.y;
    }
    return vec2(d,m);
}

//@iq hsv2rgb
vec3 hsv2rgb( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0,4,2),6.)-3.)-1., 0., 1. );
	return c.z * mix( vec3(1), rgb, c.y);
}

vec4 FC = vec4(0.192,0.227,0.227,0.);
vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, float last, inout float d, vec2 uv) {

    vec3 C = vec3(0);
    float m = 0.;
    vec2 ray = marcher(ro,rd,128);
    d=ray.x;m=ray.y;

    hitPoint = hit;  
    shs = ghs;
    sd = gd;

    if(d<MAX_DIST)
    {
        vec3 p = ro + rd * d;
        vec3 n = normal(p,d);

        vec3 lpos =vec3(15.,35.,35.);
        vec3 l = normalize(lpos-p);

        float diff = clamp(dot(n,l),.09,.99);

        float shdw = 1.;
        for( float t=.01; t < 12.; ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 12.*h/t);
            t += h;
            if( shdw<MIN_DIST ) break;
        }
        diff = mix(diff,diff*shdw,.65);

        vec3 h = vec3(.25);

        if(m==1.) {
            vec2 hp = hitPoint.xz*rot(.78);
            vec2 f = fract(hp*24.)-.5;
            h = mix(vec3(.15),vec3(.2),mod(floor(f.x),3.) == 0. ? 1. : 0.);  

            float px = 4./R.x;
            float d = smoothstep(px,-px,abs(sd-.125)-.015);
            h = mix(h, vec3(.275), d);
            if(p.y<-.01) h = vec3(.1);
            ref = h;
        }

        if(m==2.) { h = hsv2rgb(vec3(shs+(p.x*.05)+T*.08,.85,.5)); ref = h; }
        if(m==3.) { h = vec3(.025); ref = vec3(.35); }
        if(m==4.) { h = vec3(.125); ref = vec3(.25); }

        C = (diff*h);

        ro = p+n*.005;
        rd = reflect(rd,n);
    } 
    return vec4(C,d);
}

void mainImage( out vec4 O, in vec2 F )
{   
    // precal
    tspeed = T*.6;
    tmod = mod(tspeed,12.);

    float t1 = lsp(00.,02.,tmod);
    t1 = eoc(t1); t1 = t1*t1*t1;

    float t2 = lsp(03.,05.,tmod);
    t2 = eoc(t2); t2 = t2*t2*t2;

    float t3 = lsp(06.,8.,tmod);
    t3 = eoc(t3); t3 = t3*t3*t3;

    float t4 = lsp(9.,11.,tmod);
    t4 = eoc(t4); t4 = t4*t4*t4;

    ga1 = (t1*db)-(t3*db);
    ga2 = (t2*db)-(t4*db);

    ga3 = (t1-t3);
    ga4 = (t2-t4);

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,3.);
    vec3 rd = normalize(vec3(uv,-1));

    float x = M.xy==vec2(0) ? 0. : -(M.y/R.y*.125-.0625)*PI;
    float y = M.xy==vec2(0) ? 0. : -(M.x/R.x*.5-.25)*PI;

    mat2 rx = rot(-.92-x), ry = rot(y+(ga3-ga4)*PI);
    ro.zy *= rx; ro.xz *= ry; 
    rd.zy *= rx; rd.xz *= ry;

    // reflection loop (@BigWings)
    vec3 C = vec3(0);
    vec3 ref=vec3(0), fil=vec3(.95);
    float d =0.,a=0.;

    for(float i=0.; i<2.; i++) {
        vec4 pass = render(ro, rd, ref, i, d, uv);
        C += pass.rgb*fil;
        fil*=ref;
        if(i==0.)a=pass.w;
    }

    C = mix(FC.rgb,C,exp(-.0008*a*a*a));
    C=pow(C, vec3(.4545));
    O = vec4(C,1);
}

