float opUniS(float a,float b,float r){
	float h = max(r-abs(a-b),0.)/r;
    return min(a,b)-h*h*h*r*(1./6.);
}

float sdCylinder(vec3 p, float h, float r){
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.)+length(max(d,0.));
}

float sdBox(vec3 p, vec3 b){
  vec3 q = abs(p)-b;
  return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)),0.);
}

float opPipeJointT(vec2 p, float d, float t, float l, vec2 dir){

    d = abs(d-t*.5)-t*.5;              // sdf: body (skin of the pipes sdf)

    p=dir*p + (1.-abs(dir))*abs(p);    // (dir==vec2(0)) ? abs(p) : dir*p
    float cps = max(p.x, p.y)-l-t;     // sdf: cutting planes

#if 0
    if(cps>0.){
        if(d<0.)
            return cps;
        else
            return length(vec2(d, cps));
    }
    return d;
#else
    float e = length(vec2(d, cps));    // sdf: edges
    float c = mix(cps, e, step(0.,d)); // sdf: caps (cutting planes + edges)
    return mix(d, c, step(0.,cps));    // sdf: the joint (caps + body) 
#endif
}

float map(vec3 p){
    const float r = .25;
    const float l = 3.;
    float jl = 1.+sin(iTime*5.)*.5;
    float cr = .1+sin(iTime*2.)*.1;

