#define NONE  -1
#define PLANE  0
#define SPHERE 1

#define TMAX 10000.

const float N = 55.0;

const vec4 skyTop = vec4(0.001f, 0.0f, 0.1f, 1.0f);
const vec4 skyBottom = vec4(0.55f, 0.08f, 0.896f, 1.0f);

float gridTextureGradBoxFilter(vec2 uv, vec2 ddx, vec2 ddy) {
    uv += 0.5f;
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;
    vec2 a = uv + 0.015 + w;
    vec2 b = uv - 0.015 + w;

    vec2 i = (floor(a) + min(fract(a) * N, 1.0) -
        floor(b) - min(fract(b) * N, 1.0)) / (N * w);

    return (1.0 - i.x) * (1.0 - i.y);
}

float gridTexture(vec2 uv) {
    uv += 0.5f;
    vec2 i = step(fract(uv), vec2(1.0 / N, 1.0 / N));
    return (1.0 - i.x) * (1.0 - i.y);
}

vec2 texCoords(vec3 pos, int objectType) {
    vec2 uv;
    if(objectType == PLANE) {
        uv = pos.xz;
    }

    uv.y -= iTime * 10.;
    return 0.5 * uv;
}

float traceRay(vec3 rayOrigin, vec3 rayDir, inout vec3 pos, inout vec3 nor, inout int objType) {
    float tmin = TMAX;
    pos = vec3(0.0f, 0.0f, 0.0f);
    nor = vec3(0.0f, 0.0f, 0.0f);
    objType = NONE;

    float t = (-1.0 - rayOrigin.y) / rayDir.y;

    if(t > 0.0) {
        tmin = t;
        nor = vec3(0.0f, 1.0f, 0.0f);
        pos = rayOrigin + rayDir * t;
        objType = PLANE;
    }

    return tmin;
}

void createRay(in vec2 pixel, inout vec3 rayOrigin, inout vec3 rayDirection) {
    vec2 p = (2. * pixel.xy - iResolution.xy) / iResolution.y;

    vec3 camPos = vec3(0.1f, 1.0f, 5.0f);
    vec3 camDir = vec3(0.1f, 1.0f, 0.0f);

    vec3 dir = normalize(camDir - camPos);
    vec3 right = normalize(cross(dir, vec3(0.0f, 1.0f, 0.0f)));
    vec3 up = normalize(cross(right, dir));

    rayDirection = normalize(p.x * right + p.y * up + 3.0f * dir);
    rayOrigin = camPos;
}

float rand(float x) {
    return fract(sin(x) * 50000.0f);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 p = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;

    float radius = 0.6f;
    vec2 ctr = vec2(0.0f, 0.3f);
    vec2 diff = p - ctr;

    float width = 40.0;
    float skylineHeight = rand((mod(trunc(p.x * width), width)));

    float falloffFactor = 0.8;
    skylineHeight *= 1.0 - abs(p.x) * falloffFactor;

    float t = TMAX;
    if(p.y > 0.0 && p.y * 2.7 < skylineHeight) {
        fragColor = vec4(0.216, 0.165, 0.165, 1.0);
        fragColor *= rand(p.y) * rand(p.x) * 3.0f;
        t = 50.0f;
    } else if(dot(diff, diff) < (radius * radius) && p.y > 0.0f) {
        fragColor = vec4(1.0f, 0.55f, 0.28f, 1.0f) * step(fract(p.y * 10.) - p.y / 4., 8. / 10.0);
        t = 5.0;
    } else {
        fragColor = mix(skyTop, skyBottom, .25 - p.y / 3.);
    }

    vec3 rayDir;
    vec3 rayOrigin;
    vec3 rayOriginDdx;
    vec3 rayDirDdx;
    vec3 rayOriginDdy;
    vec3 rayDirDdy;

    createRay(fragCoord, rayOrigin, rayDir);
    createRay(fragCoord + vec2(1.0, 0.0), rayOriginDdx, rayDirDdx);
    createRay(fragCoord + vec2(0.0, 1.0), rayOriginDdy, rayDirDdy);

    vec3 pos;
    vec3 nor;
    int objectType = NONE;

    float groundt = traceRay(rayOrigin, rayDir, pos, nor, objectType);

    t = min(groundt, t);

    vec3 posDdx = rayOriginDdx - rayDirDdx * dot(rayOriginDdx - pos, nor) / dot(rayDirDdx, nor);
    vec3 posDdy = rayOriginDdy - rayDirDdy * dot(rayOriginDdy - pos, nor) / dot(rayDirDdy, nor);

    vec2 uv = texCoords(pos, objectType);

    vec2 uvDdx = texCoords(posDdx, objectType) - uv;
    vec2 uvDdy = texCoords(posDdy, objectType) - uv;

    if(objectType == PLANE) {
        float color = gridTextureGradBoxFilter(uv, uvDdx, uvDdy);
        fragColor = mix(vec4(217.0 / 255.0, 10.0 / 255.0, 217.0 / 225.0, 1.0f), vec4(133.0 / 255.0, 46.0 / 255.0, 106.0 / 255.0, 1.0f), color);
    }

    if(t < TMAX) {
        fragColor = mix(fragColor, skyBottom, 1.0 - exp(-0.0001 * t * t));
    }
    return;
}

float sn(float f) {
    return 0.5 + 0.5 * sin(f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float speed = 10.0;

    vec3 col = vec3(1.0)
        + 0.5 * vec3(1.0, 0.1, 0.5) * floor(sin((cos(iTime) * uv.x) * 100.0 + iTime * speed))
        + 0.2 * vec3(0.0, 0.1, 0.5) * floor(sin((sin(iTime) * uv.y) * 100.0 - iTime * speed));
    fragColor = vec4(col,1.0);
}

// https://iquilezles.org/articles/distfunctions2d/
float sdRoundedBox( vec2 p0, vec2 c, vec2 b, vec4 r )
{
    vec2 p = p0-c;
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float arrayBox(vec2 p, vec2 size, vec2 spacing)
{

    vec2 arraySpacing = vec2(2. * (size + spacing));
    vec2 asize = size + spacing;
    vec2 pmin = floor(0.5 * p / asize) * asize;
    vec2 pmax = ceil(0.5 * p / asize) * asize;
    vec2 center = (pmin + pmax) / 2.0;


    vec2 r = mod(p, arraySpacing);
    vec2 boxDims = vec2(size);
    float d = sdRoundedBox(r+vec2(0., -size.y*0.5), vec2(size+spacing), boxDims, vec4(0.015,0.005,0.015,0.005));
    float d2 = sdRoundedBox(r+vec2(0., size.y*1.), vec2(size+spacing), vec2(size.x*1.1, size.y/4.), vec4(0.0075));

    return opSmoothUnion(d, d2, 0.0);

}

float SceneDist(vec2 p, vec2 size, vec2 spacing) {
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y / 2.0;

    vec2 actualSize = size + spacing;

    float d = arrayBox(p, size, spacing);

    return d;
}

vec4 rowOfSeats(vec2 p, vec2 size, vec2 spacing) {


    // box masking 
    vec2 actualSize = size + spacing;

    vec2 pmin = floor(0.5 * p / actualSize) * actualSize;
    vec2 pmax = floor(0.5 * p / actualSize) * actualSize;
    vec2 pcenter = mix(pmin, pmax, 0.5);


    float d = SceneDist(p, size, spacing) * 255.;
    vec3 rgb1 = vec3(52, 117, 199) / 255.;
    vec3 rgb2 = vec3(219, 87, 47) / 255.;
    vec3 rgb3 = vec3(0.);
    vec3 col1 = mix( rgb3, rgb2, 1.0-smoothstep(0.0,1.,d) );
    vec3 col2 = mix( rgb3, rgb1, 1.0-smoothstep(0.0,1.,d) );
    col1 =  col1 + vec3(0.07) * round(texture(iChannel0, pcenter).r);
    col2 =  col2 + vec3(0.07) * round(texture(iChannel0, pcenter).r);

    // mouse
    //vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y / 2.0;
    vec2 m = vec2(0.95 * sin(0.25*iTime), 0.5 * sin(0.5*iTime));


    float l = length(pcenter-m * 0.5);

    if (abs(pcenter.x-0.25) <= 0.01 || abs(pcenter.x+0.5) <= 0.01) {
        return vec4(0.);   
    } else {
       float t = clamp(l/0.3-0.75, 0.0, 1.0);
       return vec4(mix(col1, col2, smoothstep(0., 1., 25.*t)), 1.0);

    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 1.25 * (fragCoord - 0.5*iResolution .xy)/iResolution.y;

    vec2 size = vec2(0.03, 0.025);
    vec2 spacing = vec2(0.005, 0.015);

    vec4 seats = rowOfSeats(uv, size, spacing);


    fragColor = max(seats, 0.15);

}

// The simplest 2D pixel traversal algorithm - advancing to the next
// pixel is just two float additions (and a floor() to get the integer
// coordinate). I accidentally discovered this method while trying to
// come up with an algorithm for voxel traversal that only used integer
// arithmetic. The one caveat is that when the ray passes exactly
// through the corner of a pixel, it ends up double counting a pixel,
// consequently not being watertight (uncomment lines 67/68 to see an
// example). This could be fixed with some extra manual checks but at
// that point it might just be more worth it to use the regular voxel
// traversal algorithm.

// It works by finding an alternative parameterization of the input ray,
// R(T) = O + D * T, that for successive integer values of T returns
// points in order of the pixels pierced by the original ray. Such a
// parameterization unfortunately does not exist in 3D.

#define RES_Y 9.0
vec2 _p;
vec2 _g;
float _s;
bool _b = false;
float _c = 0.0;

void visitPixel(vec2 p) {
    _b = _b || p == _p;
}

void plotCircle(vec2 p) {
    _c += max(RES_Y / _s * 0.01 - distance(p / _s, _g), 0.0);
    _c = min(_c, 1.0);
}

void traverse(vec2 o, vec2 d, float tMax) {
    vec2 s = 1.0 - 2.0 * step(d, vec2(0.0)); o *= s; d *= s;
    // N is the number of pixels to visit, it's
    // 1 + manhattan distance between floor(r(0)) and floor(r(tMax))
    int N = int(dot(abs(floor(o + d * tMax) - floor(o)), vec2(1.0))) + 1;
    vec2 O = s * (o * d.yx + d * (floor(o) + 1.0 - fract(o.yx))) / (d.x + d.y);
    vec2 D = s * d / (d.x + d.y);
    // R(T) = O + D * T
    vec2 P = O;

    for (int n = 0; n < N; ++n) {
        visitPixel(floor(P));
        plotCircle(P);
        P += D;
    }
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float s = RES_Y / iResolution.y; _s = s;
    _p = floor(fragCoord * s);
    _g = fragCoord;
    vec2 o, d;

    if (iMouse.z > 0.0) {    
        o = abs(iMouse.zw) * s;
        d = iMouse.xy * s - o;
    } else {
        vec2 p1 = iResolution.xy * s * (vec2(cos(iTime * 1.3), sin(iTime * 1.2)) * 0.4 + 0.5);
        vec2 p2 = iResolution.xy * s * (vec2(sin(iTime * 1.3), cos(iTime * 0.6)) * 0.4 + 0.5);
        o = p1;
        d = p2 - p1;
    }

    // Uncomment to see corner intersection issue
    // o = vec2(0.0);
    // d = 4.0 * vec2(3.0, 2.0);

    traverse(o, d, 1.0);

    if ((int(_p.x) + int(_p.y)) % 2 == 0) {
        fragColor.rgb = vec3(0.918,0.894,0.843);
    } else {
        fragColor.rgb = vec3(0.9647, 0.949, 0.906);
    }

    if (_b) {
        fragColor.rgb *= vec3(0.471, 0.549, 0.471);
    }

    vec2 pa = fragCoord - o / s;
    d /= s;
	float h = clamp(dot(pa, d) / dot(d, d), 0.0, 1.0);
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.0, 0.0), clamp(1.5 - length(pa - h * d), 0.0, 1.0));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.745,0.937,0.792), _c);
}

/* Golfed version of my previous shader https://www.shadertoy.com/view/mtyGWy
   I also simplified 6.28318 (2*PI) to 6. which slightly offsets the colors.

[533 -> 227 chars] */
void mainImage(out vec4 O, vec2 F) {
    vec2  R = iResolution.xy,
          u = (F+F-R) / R.y;
    float i,  l = length(u);

    for (O *= i; i++ < 4.;

         O += pow(.04 / 
                 abs(
                     sin(
                         8. * ( length( u = fract(u * 1.5) - .5 ) / exp(l) ) 
                         + iTime
                        ) // sin
                    )  // abs
              , 1.2) // pow

              * (1. + cos(6. * ( l + (i + iTime)*.4 + vec4(.26,.4,.56,0) )))
    );
}

//To change camera settings or the scene, go to the 'Buffer A' tab.
//To change the bloom threshold settings, go to the 'Buffer B' tab.
//To set or rwmove the maximum number of frames or objects go to the 'Common' tab.

#define Bloom

#define exposure .8

//I used the Bing AI to improve this code.
vec3 blur(sampler2D s, vec2 p, int w) {
  vec2 invRes = 1. / vec2(textureSize(s, 0));
  vec3 color = vec3(0.);
  float n = 1./float(2*w*w);
  const float invpi = 1./PI;
  for (int x = -w; x <= w; x++) {
    for (int y = -w; y <= w; y++) {
      vec2 uv = p + vec2(x, y) * invRes;
      float weight = n*invpi*exp(-float(x*x+y*y)*n);
      //float weight = 1.;
      color += texture(s, uv).xyz*weight;
    }
  }
  return color;
}

#define size 2.25*((R.x+R.y)*.00119048)
void mainImage( out vec4 o,  vec2 i )
{
    vec2 p = R-10.;
    #ifdef maxFrame
    if(i.x>=p.x&&i.y>=p.y&&iFrame>maxFrame){o=vec4(1.);return;}
    #endif
    o = texelFetch(iChannel0,ivec2(i),0);
    o /= o.w;

    #ifdef Bloom
    #ifdef maxFrame
    if(iFrame>=maxFrame)
    #endif
    o.xyz += blur(iChannel1,i/R,int(size));
    #endif


    o.xyz *= exposure;

    o.xyz = ACESFilm(o.xyz);
    o.xyz = linearToSRGB(o.xyz);
}

