#define PI 3.14159265

#define PEN_THICKNESS 0.005
#define FADE_RATE 0.002


// Line segment SDF by iq
// https://iquilezles.org/articles/distfunctions2d/

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

///////////////////////////////////////////////////


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;

    vec2 penPos = texelFetch(iChannel1, ivec2(0), 0).xy;
    vec2 lastPenPos = penPos;
    if (iFrame > 1) {
        lastPenPos = texelFetch(iChannel1, ivec2(1,0), 0).xy;
    }
    float penSDF = sdSegment(p, lastPenPos, penPos) - PEN_THICKNESS;
    float penStrength = smoothstep(-0.005, 0.005, -penSDF);
    vec3 penCol = 0.5 + 0.5 * sin(float(iFrame) * 0.1 + vec3(0,2,4));

    vec4 col = texture(iChannel0, uv);
    col.a *= 1.0 - FADE_RATE;
    col.rgb = mix(col.rgb, penCol, penStrength);
    col.a = max(col.a, penStrength);

    fragColor = col;
}

