vec4 path(vec4 p)
{
    float dt = 0.001;
    vec2 grav1Pos = grav1;
    vec2 grav2Pos = grav2;

    for (int i = 0; i < 50; i++) {
        p.xy += p.zw * dt;
        p.zw -= normalize(p.xy - grav1Pos) / max(length(p.xy - grav1Pos), 0.01) * dt;
        p.zw -= normalize(p.xy - grav2Pos) / max(length(p.xy - grav2Pos), 0.01) * dt;
    }

    return p;
}

