// Distance macros
#define m(p) d = min(d, length(uv - p));
#define l(p, q) d2 = min(d2, seg(uv, p, q));

// iq's IK
vec2 solve(vec2 p, float r1, float r2, float side) {
    float h = dot(p,p);
    float w = h + r1*r1 - r2*r2;
    float s = max(4.0*r1*r1*h - w*w,0.0);
    return (w*p + side * vec2(-p.y,p.x)*sqrt(s)) * 0.5/h;
}

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 drawLeg(vec2 uv, float sc, float t) {
    // Points
    vec2 p = sc * vec2(38, 7.8);
    vec2 pRot = p + sc * 15. * Dir(2. * t);
    vec2 pIK1 = solve(pRot, sc * 41.5, sc * 50.0,  1.);
    vec2 pIK1T = sc * 40.1 * Dir(atan(pIK1.y, pIK1.x) + 1.506);
    vec2 pIK2 = solve(pRot, sc * 39.3, sc * 61.9, -1.);
    vec2 pIK3 = pIK1T + solve(pIK2 - pIK1T, sc * 39.4, sc * 36.7, - 1.);
    float a = atan(pIK3.y-pIK2.y, pIK3.x-pIK2.x);
    vec2 pIK3T = pIK2 + sc * 49. * Dir(a + 1.73);

    // Point distances (could use dot then sqrt for better performance)
    float d = length(uv);
    m(p);
    m(pRot);
    m(pIK1);
    m(pIK1T);
    m(pIK2);
    m(pIK3);
    m(pIK3T);

    // Segment distances
    float d2 = 1e5;
    l(vec2(0), pIK1);
    l(vec2(0), pIK1T);
    l(vec2(0), pIK2);
    l(pIK1, pIK1T);
    l(pIK1, pRot);
    l(p, pRot);   
    l(pIK2, pRot);    
    l(pIK2, pIK3);
    l(pIK2, pIK3T);
    l(pIK1T, pIK3);
    l(pIK3, pIK3T);    

    // Draw
    float s = ss(1., 0.0125 - d);
    float s2 = ss(1., 0.005 - d2);
    return vec2(s, s2);
}

void mainImage( out vec4 O, in vec2 px )
{
    vec2 uv = (px - 0.5 * res) / res.y;
    float t = iTime;

    float sc = 0.0058;

    vec3 col = vec3(0);
    const float n = 2.;
    for (float i = 0.; i < n; i++) {
        float io = (i+1.) / n;
        vec2 leg = drawLeg(uv, sc, t + io * pi);
        vec3 cLeg = vec3(max(leg.x, leg.y), leg.xx);
        col = max(col, io * cLeg);
    }

    O = vec4(col, 1);
}

/*
