void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; 
    mat4 bayer = mat4(
        0.0000, 0.5000, 0.1250, 0.6250,
        0.7500, 0.2500, 0.8750, 0.3750,
        0.1875, 0.6875, 0.0625, 0.5625,
        0.9375, 0.4375, 0.8125, 0.3125
    );

    uv += vec2(iTime * .02);
    vec3 col = texture(iChannel0, uv).rgb;

    // greyscale pixel by calculating average of all channels
    float avg = (col.r + col.g + col.b) / 3.;

    // calculate coord in bayer matrix using modulo (4 as size of matrix)
    // if greyscale darker than threshold in matrix use black else white
    fragColor = vec4(bayer[int(fragCoord.x) % 4][int(fragCoord.y) % 4] < avg);
}

#define S(v) smoothstep( 4./R.y, -4./R.y, abs(v)-.025)  // AA draw thing stroke
#define H(p) fract(sin(dot(p+1.7*floor(t/2.), vec2(12.9898, 78.233))) * 43758.5453)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = 2.* ( u+u - R ) / R.y,
          I = floor(U+.5), Ic = mod(floor(2.*U),2.), // tile & subtile Id
          F = fract(U)-.5,
          A = abs(F),                            // all var#2 = ring continuing on neightbor subtile
          F2 = F; int j,i = A.x>A.y ? 0: 1 ; F2[i] -= sign(F[i]);
    float l = (length(F )-.05)*.78,  n = mod(l ,.1) - .05, N=-1., // ring Id
         l2 = (length(F2)-.05)*.78, n2 = mod(l2,.1) - .05, 
          a = atan(F.y/F.x)/3.14,   a2 = atan(F2.y/F2.x)/3.14, b=a,b2=a2,
          t = iTime/3., T = fract(int(t)%2<1?t:-t), T0 = T-1.;//-9 -T T-1 sequences over time
          i = int(Ic.x+Ic.y) % 2, j=i;           // subtile mode
  //int s = int(t)%4;                            // sequences over time
  //if (s%2>0) T = 1.-T,  T0=T-1.;
  //if (s<2) T0=T-1.; else T0 = 1.-T;
    O *= 0.;
    if ( l < 0. ) return;
  //if ( mod(I.x+I.y  ,2.) < 1. ) O.b++;         // debug
  //if ( mod(Ic.x+Ic.y,2.) < 1. ) O.g+=.3;       // debug

    if ( l < .2 )  O += S(n),N=l;                // ring 1, 2 ( center )

    if ( H(I+.3)<.5 )                            // ring 3, close
             { if ( l>.2 && l < .3 && b  > T0 && b  < T )  O.r += S(n ), N=l; }
    else {                                       // ring 3, connected
        if ( H(I)<.5 ) b = -a, b2=-a2, j=1-i;
        if ( j<1 )                          
             { if ( l > .2 && l < .3 && b > T0 && b < T )  O.r += S(n ), N=l; 
               if ( l > .4           && b > T0 && b < T )  O.r += S(n ), N=l;  // ring 5 = 3 from neighbor
             }
        else   if ( l2> .4 && l2< .5 && b2> T0 && b2< T )  O.g += S(n2), N=l2; // ring 5 = 3 from neighbor
    }
    if (length(O)==0.) {                         // ring 4
     // if ( H(I+.0)<.5 ) b = -a, b2=-a2, j=1-i; else b=a,b2=a2, j=i;
        if ( j<1 ) 
             { if ( l > .3 && l < .4 && b > T0 && b < T )  O.r += S(n ), N=l; }
        else   if ( l2< .4           && b2> T0 && b2< T )  O.g += S(n2), N=l2;
    }    
                                                // color scheme. comment for debug
    O = max(O.r,O.g) * ( int(floor(N/.1))%2 > 0 ? vec4(1,.5,0,0) : vec4(1) );
}

