    if (fragCoord.y < 1.0 && fragCoord.x > 1.0 && fragCoord.x < 2.0) {
        fragColor = lastState;
    } else if (fragCoord.x < 1.0 && fragCoord.y < 1.0) {
        fragColor = path(lastState);
    } else {
        discard;
    }
}

