// I tried to come up with an improved stars-from-noise algorithm
// and I came up with this. 
// instead of just directly sampling from the noise texture and
// raising it to a high power to only allow bright texels through,
// it snaps the input UV to the center of each pixel (effectively
// forcing nearest-sampling) and then uses the distance from the
// actual UV coord to the centered UV coord to make the stars circular.
// This has the effect of eliminating the artifacts from the other
// method where stars sort of blend into each other and have weird shapes.
vec3 calcStars(vec3 dir)
{
    const vec3 moonCol = vec3(1.0, 1.0, 0.85);
    const float moonSize = 0.04;
    const vec2 moonPos = vec2(0.25, 0.2);

    vec2 noiseTexRes = iChannelResolution[0].xy;

    dir.yz *= transpose(planetRot);
    int faceIndex;
    vec2 uv = sampleCube(dir, faceIndex);
    vec2 starsUV = (floor(uv * noiseTexRes) + 0.5) / noiseTexRes; // Snaps the uv to a texel center
    vec2 texelOffset = (uv - starsUV) * noiseTexRes;
    float starIntensity = pow(clamp(texture(iChannel0, starsUV).x, 0.0, 1.0), 50.0);

    starIntensity *= 1.0 - pow(min(length(texelOffset * 4.0), 1.0), starIntensity);
    vec3 col = vec3(starIntensity);
    if (faceIndex == 5) {
        float moonDist1 = length(uv - moonPos) - moonSize;
        float moonDist2 = length(uv - moonPos - vec2(moonSize * 0.35)) - moonSize;

        float moonFactor1 = smoothstep(-0.003, 0.003, -moonDist1);
        float moonFactor2 = smoothstep(-0.003, 0.003, moonDist2);
        col = mix(col, moonCol * moonFactor1 * moonFactor2, moonFactor1);
    }
    return col;
}

vec3 sky(vec3 dir)
{

    vec3 stars;
    if (dir.y > 0.0) {
        stars = calcStars(dir);
        stars *= smoothstep(-0.1, 0.4, sunDir.y); // Fade out stars for daytime
    }
    dir.y = max(dir.y, 0.001);
    vec3 preetham = max(calculateSkyLuminanceRGB(-sunDir, dir, 2.5) / 25.0, 0.0);
    preetham *= clamp(0.3-sunDir.y, 0.0, 1.0) * 0.5 + 0.5; // Darken sky more during night
    return preetham + stars;
}







//////////////////////////////////////////////////////////////////////////
// The following code is from
// http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
//////////////////////////////////////////////////////////////////////////

vec2 seed = vec2(0.0);

vec2 rand2n() {
    seed+=vec2(-1,1);
	// implementation based on: lumina.sourceforge.net/Tutorials/Noise.html
    return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
		fract(cos(dot(seed.xy ,vec2(4.898,7.23))) * 23421.631));
}

vec3 ortho(vec3 v) {
    //  See : http://lolengine.net/blog/2013/09/21/picking-orthogonal-vector-combing-coconuts
    return abs(v.x) > abs(v.z) ? vec3(-v.y, v.x, 0.0)  : vec3(0.0, -v.z, v.y);
}

vec3 getSampleBiased(vec3  dir, float power) {
	dir = normalize(dir);
	vec3 o1 = normalize(ortho(dir));
	vec3 o2 = normalize(cross(dir, o1));
	vec2 r = rand2n();
	r.x=r.x*2.*PI;
	r.y=pow(r.y,1.0/(power+1.0));
	float oneminus = sqrt(1.0-r.y*r.y);
	return cos(r.x)*oneminus*o1+sin(r.x)*oneminus*o2+r.y*dir;
}

//vec3 getSample(vec3 dir) {
//	return getSampleBiased(dir,0.0); // <- unbiased!
//}

vec3 getCosineWeightedSample(vec3 dir) {
	return getSampleBiased(dir,1.0);
}

vec3 getConeSample(vec3 dir, float extent) {
        // Formula 34 in GI Compendium
	dir = normalize(dir);
	vec3 o1 = normalize(ortho(dir));
	vec3 o2 = normalize(cross(dir, o1));
	vec2 r =  rand2n();
	r.x=r.x*2.*PI;
	r.y=1.0-r.y*extent;
	float oneminus = sqrt(1.0-r.y*r.y);
	return cos(r.x)*oneminus*o1+sin(r.x)*oneminus*o2+r.y*dir;
}

