// By iq
// from https://iquilezles.org/articles/distfunctions/
float sdCutHollowSphere( vec3 p, float r, float h, float t )
{
  // sampling independent computations (only depend on shape)
  float w = sqrt(r*r-h*h);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  return ((h*q.x<w*q.y) ? length(q-vec2(w,h)) : 
                          abs(length(q)-r) ) - t;
}

// By iq
// from https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// By iq (modified)
// from https://iquilezles.org/articles/distfunctions/
vec3 opRep( in vec3 p, in vec3 c )
{
    return mod(p+0.5*c,c)-0.5*c;
}

// By iq
// from https://iquilezles.org/articles/distfunctions/
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}









struct Material {
    vec3 col;
    int type;
};

struct SceneObj {
    float dist;
    Material mat;
};

SceneObj opUnion(in SceneObj a, in SceneObj b)
{
    if (a.dist <= b.dist) {
        return a;
    } else {
        return b;
    }
}

SceneObj sculptureDE(vec3 p)
{
    Material redmat = Material(vec3(1.0, 0.05, 0.05), MAT_DIFFUSE);
    Material whitemat = Material(vec3(1.0), MAT_METAL);

    vec3 z = p;
    z.yz *= rotmat(-0.6);
    float cupDist = sdCutHollowSphere(z, 1.0, 0.3, 0.03);
    SceneObj cup = SceneObj(cupDist, redmat);

    const float k = 0.3;
    float metalDist = length(p) - 0.8;
    metalDist = min(metalDist, length(p - vec3(0.0, 1.0, 0.0)) - 0.7);
    metalDist = opSmoothUnion(metalDist,
        sdTorus(p.yzx - vec3(2.0,0.0,0.0), vec2(0.7, 0.2)),
        k);
    const vec3 c = vec3(0.2);
    float balls = length(opRep(p, c)) - 0.1;
    metalDist = max(metalDist, balls);
    SceneObj metal = SceneObj(metalDist, whitemat);
    metal.mat.col = (0.5 + 0.5 * cos(round(p / c))).zyy;

    SceneObj sculp = opUnion(cup, metal);

    return sculp;
}

float sceneDE(vec3 p, out Material mat)
{
    SceneObj sculpObj = sculptureDE(p);

    Material floorMat = Material(vec3(1.0), MAT_METAL);
    float floorDispl = sin(length(p.xz * 10.0) - iTime) * 0.03;
    floorDispl /= max(length(p.xz), 1.0);
    float floorDist = p.y + 1.0 + floorDispl;
    SceneObj floorObj = SceneObj(floorDist, floorMat);

    SceneObj scene = opUnion(sculpObj, floorObj);
    mat = scene.mat;
    return scene.dist * 0.5;
}

vec3 calcNormal(vec3 p)
{
    const vec2 eps = vec2(0.001, 0.0);
    Material temp = Material(vec3(0.0), 0);
    float centerDist = sceneDE(p, temp);
    vec3 v = vec3(sceneDE(p + eps.xyy, temp),
                  sceneDE(p + eps.yxy, temp),
                  sceneDE(p + eps.yyx, temp)) - centerDist;

    return normalize(v);

}

bool raymarch(in vec3 ro, in vec3 rd, out vec3 hit, out vec3 norm, out Material mat)
{
    float t = 0.0;
    vec3 p = ro;
    for (int i = 0; i < RAYMARCH_MAX && t < RAYMARCH_MAX_DIST; i++) {
        float dist = sceneDE(p, mat);
        if (dist < RAYMARCH_EPS) {
            norm = calcNormal(p);
            hit = p;
            return true;
        }
        t += dist;
        p += dist * rd;
    }
    return false;
}






/////////////////////////////////////////////////////////////////////
// Most of this function was taken from
// http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
/////////////////////////////////////////////////////////////////////

vec3 pathtrace(vec3 ro, vec3 rd)
{
    float sunSize = 1E-3; // Hemisphere area covered by the sun
    float sunStrength = max(-sunDir.y, 0.0); // To make sunsets be darker then midday

    vec3 luminance = vec3(1.0);
    vec3 direct = vec3(0.0);

    for (int i = 0; i < PATHTRACE_MAX; i++) {
        vec3 hit = vec3(0.0);
        vec3 norm = vec3(0.0);
        Material mat = Material(vec3(0.0), 0);
        if (raymarch(ro, rd, hit, norm, mat)) {
            vec3 newStartPos = hit + norm * RAYMARCH_EPS * 2.0;
            if (mat.type == MAT_DIFFUSE) {
                rd = getCosineWeightedSample(norm);
                luminance *= mat.col;
                // Direct lighting
                vec3 sunSampleDir = getConeSample(-sunDir,sunSize);
                float sunLight = dot(norm, sunSampleDir);
                vec3 temp1;
                vec3 temp2;
                Material temp3;
                if (sunLight>0.0 && !raymarch(newStartPos,sunSampleDir, temp1, temp2, temp3)) {
                    direct += luminance*sunLight*sunStrength;
                }
                ////////////////////////////
            } else if (mat.type == MAT_METAL) {
                rd = reflect(rd, norm);
                luminance *= mat.col;
            }
            ro = newStartPos;
        } else {
            vec3 backgroundCol = sky(rd) + pow(max(dot(rd, -sunDir), 0.0), 200.0);
            return direct + luminance * backgroundCol;
        }
    }

    return direct;
}

