void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 c = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;

    planetRot = rotmat(iTime * 0.25);
    sunDir.yz *= planetRot;

    vec3 ro = vec3(0.0, 0.0, -3.0); // Camera position
    vec3 rd = normalize(vec3(c, 1.0));

    vec3 col = vec3(0.0);
    for (int samp = 0; samp < SAMPLES; samp++) {
        seed = hash21(hash12(fragCoord) + float(samp) * 0.0123398 + float(iFrame));
        col += pathtrace(ro, rd);
    }
    col /= float(SAMPLES);

    fragColor = vec4(col,1.0);
}

//       
//         

#define PI 3.14159265359
#define u_radius 0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord) {

    float strength = (1.0 + sin(float(iTime)))*0.5;

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 muv = iMouse.xy / iResolution.xy;

    vec2 diff = uv - iMouse.xy / iResolution.xy;
    float distance = length(diff);

    float scale = (1.0 - cos(distance/u_radius * PI * 0.5));
    scale +=
    (
        mod(uv.x-muv.x, 0.1 * strength) +
        mod(uv.y-muv.y, 0.1 * strength)
    ) / ((1.0 + distance)*0.8);

    vec2 fishUv = iMouse.xy / iResolution.xy + normalize(diff) * u_radius * scale;

    uv = uv * (1.0 - strength) + fishUv *  strength;


    fragColor = vec4(texture(iChannel0, uv).xyz, 1.0);
}

// Neon circles blooming into a fractal hexagonal grid.
// Inspiration:
// - https://www.shadertoy.com/view/mly3Dd - Glowing concentric circles + square grid folding
// - https://www.shadertoy.com/view/NtBSRV - Hexagonal cells

//#define TETRASKELION  // if hexagons offend you

vec3 pal(float a) { return 0.5 + cos(3.0*a + vec3(2,1,0)); }  // Biased rainbow color map. Will be squared later.

vec2 fold(vec2 p) {  // Shift and fold into a vertex-centered grid.
#ifdef TETRASKELION
  return fract(p) - 0.5;
#else
  vec4 m = vec4(2,-1, 0,sqrt(3.));
  p.y += m.w/3.0;      // center at vertex
  vec2 t = mat2(m)*p;  // triangular coordinates (x , y , x+y )
  return p - 0.5*mat2(m.xzyw) * round((floor(t) + ceil(t.x+t.y)) / 3.0);  // fold into hexagonal cells
#endif
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float t = iTime / 4.0, t2 = t * 0.618034, t3 = t * 1.4142135;  // dissonant timers
  mat2 M = mat2(cos(t),sin(t), -sin(t),cos(t)) * (1.0 - 0.1*cos(t2));  // rotation and scale: 0.9 [smooth] .. 1.1 [fractal]

  vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;  // y: -1 .. 1
  float d = 0.5*length(p);  // animation phase is based on distance to center

  vec3 sum = vec3(0);
  for (float i = 0.0; i < 24.0; i++) {
    p = fold(M * p);                                            // rotate and scale, fold
    sum += pal(0.01*i - d + t2) / cos(d - t3 + 5.0*length(p));  // interfering concentric circles
    // Use pal(...)/abs(cos(...)) for additive circles. I like the interference effect without the abs.
  }

  fragColor = vec4(0.0002*sum*sum, 1);  // square the sum for better contrast
}

// sdf function to define joint axis.
float sdPipeJointCutter(vec3 p, vec3 mask, vec3 dir){
    p = dir*-p + (1.-abs(dir))*abs(p); // uni or bi direction
    p *= mask;                         // axis mask
    return max(p.x, max(p.y,p.z));     // sdf: cutting planes
}

// a fancy boolean function generates joint part of pipes
float opPipeJoint(float d, float dCutter, float t, float l){
    float dPipe = abs(d-t*.5)-t*.5;
    dCutter = dCutter-l;
    float dEdge = length(vec2(dPipe, dCutter));        // sdf: edges
    float dCap = mix(dCutter, dEdge, step(0.,dPipe));  // sdf: caps (cutting planes + edges)
    float dJoint = mix(dPipe, dCap, step(0.,dCutter)); // sdf: the joint (caps + body)
    return dJoint;
    return min(d, dJoint); // return with the pipes
}

// vec4 version vec4(dist, rgb)
vec4 opPipeJoint(vec4 dPipe, vec4 dCutter, float t, float l){
    dPipe.x = abs(dPipe.x-t*.5)-t*.5;
    dCutter.x = dCutter.x-l;
    float dEdge = length(vec2(dPipe.x, dCutter.x));        // sdf: edges
    float dCap = mix(dCutter.x, dEdge, step(0.,dPipe.x));  // sdf: caps (cutting planes + edges)
    float dJoint = mix(dPipe.x, dCap, step(0.,dCutter.x)); // sdf: the joint (caps + body)
    return (dPipe.x<dJoint) ? dPipe : vec4(dJoint, dCutter.yzw);
}

float map(vec3 p){
    float r = .3;//+sin(iTime*.5)*.1;
    float l = 3.;
    float t = .1;
    float jl = 1.+sin(iTime*2.)*.25;
    float cr = .025+sin(iTime)*.025;

    r+=iWheel.y*.0001;

    float offl = l+r*.25;
    float px = sin(iTime)*offl;
    float py = cos(iTime+PI*.5)*offl;
    float pz = cos(iTime+PI*.5)*offl;
    float x = sdCapsule(p.yxz+vec3(0,px,0),l+t,r);
    float y = sdCapsule(p.zyx+vec3(0,py,0),l+t,r);
    float z = sdCapsule(p.xzy+vec3(0,pz,0),l+t,r);

    float pipes = opUniS(x,y,t);
    pipes = opUniS(pipes,z,t);

    float dx = sign(px)*step(0.,(abs(px)-offl*.5));
    float dy = sign(py)*step(0.,(abs(py)-offl*.5));
    float dz = sign(pz)*step(0.,(abs(pz)-offl*.5));
    vec3 dir = vec3(dx,dy,dz);
    float cps = sdPipeJointCutter(p, vec3(1,1,1), dir);
    float joint = opPipeJoint(pipes-cr, cps, t-cr*2., jl)-cr;

    // rivets
    vec3 q = dir*-p + (1.-abs(dir))*abs(p);
    float rivetsX = length(vec3((mod(atan(q.z,q.y)/PI,.2)-.1)*1.5, length(vec2(q.y,q.z)), q.x)-vec3(0,r+t,jl-t))-.025;
    float rivetsY = length(vec3((mod(atan(q.z,q.x)/PI,.2)-.1)*1.5, length(vec2(q.x,q.z)), q.y)-vec3(0,r+t,jl-t))-.025;
    float rivetsZ = length(vec3((mod(atan(q.x,q.y)/PI,.2)-.1)*1.5, length(vec2(q.x,q.y)), q.z)-vec3(0,r+t,jl-t))-.025;
    float rivets = max(min(rivetsX,min(rivetsY,rivetsZ)), cps-jl);

    RED(x);
    GREEN(y);
    COBALT(z);
    YELLOW(joint);
    ORANGE(rivets);

    joint = min(joint, rivets);
    return min(pipes, joint) * .9;
}

render();

