const int K_PAD_8 = 104;
const int K_PAD_9 = 105;

const int K_NUM_0 = 48;
const int K_NUM_1 = 49;
const int K_NUM_2 = 50;
const int K_NUM_3 = 51;
const int K_NUM_4 = 52;
const int K_NUM_5 = 53;
const int K_NUM_6 = 54;
const int K_NUM_7 = 55;
const int K_NUM_8 = 56;
const int K_NUM_9 = 57;

const int K_W = 87;
const int K_A = 65;
const int K_S = 83;
const int K_D = 68;
const int K_E = 69;
const int K_Q = 81;
const int K_T = 84;
const int K_N = 78;
const int K_M = 77;
const int K_C = 67;
const int K_I = 73;
const int K_X = 88;
const int K_Y = 89;
const int K_Z = 90;

const int K_SHIFT = 16;
const int K_CTRL = 17;
const int K_ALT = 18;

#define ID(c) (distance(C, c)<1.)
#define getState(k)    ((texelFetch( iChannel0, ivec2(k, 0), 0 ).x)>.5)
#define getKeypress(k) ((texelFetch( iChannel0, ivec2(k, 1), 0 ).x)>.5)
#define getToggle(k)   ((texelFetch( iChannel0, ivec2(k, 2), 0 ).x)>.5)

void mainImage( out vec4 O, in vec2 C ){

    if(iFrame < 2 ){O = vec4(0);return;}

    vec4 last = texelFetch(iChannel1, ivec2(C), 0);

    if(ID(C_MOUSE)){O = (sign(iMouse.z)>.0) ? iMouse : last;return;}

    if(ID(C_CAMERA)){
        O = last;
        bool isFreeCamera = bool(last.y);
        if((getKeypress(K_NUM_0)||getKeypress(K_PAD_0)) && sign(iMouse.z)<=.0)O = vec4(0, 0, 0, 0);
        if(getState(K_NUM_1)||getState(K_PAD_1))O = vec4(1, true, 0, 0);
        if(getState(K_NUM_3)||getState(K_PAD_3))O = vec4(3, true, 0, 0);
        if(getState(K_NUM_7)||getState(K_PAD_7))O = vec4(7, true, 0, 0);
        return;}

    if(ID(C_CAM_SCALE)){
        if(getState(K_W))last-=.01;
        if(getState(K_S))last+=.01;
        O = last;
        return;}

    if(ID(C_TARGET_X)){
        if(getState(K_X) &&  getState(K_ALT))last+=.01;
        if(getState(K_X) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Y)){
        if(getState(K_Y) &&  getState(K_ALT))last+=.01;
        if(getState(K_Y) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Z)){
        if(getState(K_Z) &&  getState(K_ALT))last+=.01;
        if(getState(K_Z) && !getState(K_ALT))last-=.01;
        O = last;
        return;}
    if(ID(C_QUAD_VIEW))   {O = vec4(getToggle(K_Q));return;}
    if(ID(C_TURN_TABLE))  {O = vec4(getToggle(K_T));return;}
    if(ID(C_DEBUG_ISO))   {O = vec4(getToggle(K_I));return;}
    if(ID(C_DEBUG_COST))  {O = vec4(getToggle(K_C));return;}
    if(ID(C_DEBUG_MATCAP)){O = vec4(getToggle(K_M));return;}
    if(ID(C_DEBUG_NORMAL)){O = vec4(getToggle(K_N));return;}

    // blank pixel
    O = vec4(0,0,1,1);
}

image_code:
vec3 rgb(float lng) {
  float h = mod(6.0 * lng / DOUBLE_PI, 6.0);
  float x = 1.0 - abs(mod(h, 2.0) - 1.0);
  if (h < 1.0)
    return vec3(1.0, x, 0.0);
  if (h < 2.0)
    return vec3(x, 1.0, 0.0);
  if (h < 3.0)
    return vec3(0.0, 1.0, x);
  if (h < 4.0)
    return vec3(0.0, x, 1.0);
  if (h < 5.0)
    return vec3(x, 0.0, 1.0);
  return vec3(1.0, 0.0, x);
}

vec2 circleDistance(in vec2 cur, in vec2 origin, in float size) {
    float aspect = iResolution.x / iResolution.y;
    return (cur - origin) / size * vec2(aspect, 1.0);
}

vec2 inverseProject(in vec2 circle) {
    float cosC = sqrt(1.0 - pow(length(circle), 2.0));
    float lat = circle.y * HALF_PI;
    float lng = atan(circle.x / cosC);
    return vec2(lat, lng);
}

vec3 rainbowBall(in vec2 cur, in vec2 origin, in float size, in float latBase) {
    vec2 circle = circleDistance(cur, origin, size);
    if (length(circle) > 1.0) return vec3(0.0);
    vec2 latLng = inverseProject(circle);
    float lng = latLng[1] + latBase;
    float shade = sqrt(1.0 - pow(length(circle), 2.0));
    float p = mod(latLng[0], PARALLEL) < 0.015 || mod(latLng[1] + latBase, PARALLEL) < 0.015 ? 0.8 : 1.0;
    return p * shade * rgb(lng);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 scaled = fragCoord / iResolution.xy;
    float xRange = 0.5 - 0.3 * iResolution.y / iResolution.x;
    vec2 origin1 = vec2(0.5, 0.3) + vec2(xRange * cos(X_SPEED * PI * iTime), 0.3 * abs(sin(Y_SPEED * PI *iTime)));
    float size = 0.2 + 0.1 * sin(iTime);
    vec3 rb = rainbowBall(scaled, origin1, size, mod(iTime, DOUBLE_PI));
    if (length(rb) > 0.)
        fragColor = vec4(rb, 1.0);
    else
        fragColor = vec4(0.2 + 0.2 * sin(iTime), scaled.y < 0.3 ? 0.3 : 0.2, 0.2, 1.0);
}

sound_code:
vec2 note(float freq, float time, float fade) {
    return vec2(sin(DOUBLE_PI * freq * time) * exp(-fade*time));
}

vec2 fullNote(float freq, float time, float fade, int count) {
    vec2 total = vec2(0.);
    for (int i = 0; i < count; i++)
        total += note(freq * float(i+1), time, fade) * pow(0.5, float(i));
    return total;
}

vec2 mainSound( int samp, float time ) {
    float deltaSinceYBounce = mod(time * Y_SPEED, 1.);
    int d1 = int(3. - 2. * cos(time * Y_SPEED / 8.));
    vec2 n1 = fullNote(55., deltaSinceYBounce, 4., d1);

    float delta2 = mod(deltaSinceYBounce + 0.5, 1.);
    int d2 = int(2. - 2. * cos(time * Y_SPEED / 4.));
    vec2 n2 = 0.2 * fullNote(55. * pow(2.,.25), delta2, 8., d2);

    float yBounceTime = time - deltaSinceYBounce;
    float xBounceRelPos = 0.5 + 0.5 * cos(X_SPEED * PI * yBounceTime);
    vec2 stereo = vec2(1. - xBounceRelPos, xBounceRelPos);

    return n1 * stereo + n2;
}

