const float PI = 3.1415926535;
const float DOUBLE_PI = PI * 2.;
const float HALF_PI = PI / 2.;
const float PARALLEL = PI / 12.;
const float BPM = 120.;
const float Y_SPEED = BPM / 60.;

const float X_SPEED = 0.5;

