#define lineWidth 0.01
#define PI 3.14159265359

vec3 lineColor(float v) {
    return vec3(v, 0.0, 0.0) + v*v;
}

vec3 genLine(float base, float p) {
    float f = abs(base - p);
    float v = lineWidth / (abs(f) + lineWidth);
    return clamp(lineColor(v), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float progress = fract(iTime*0.5);

    vec3 col = genLine(uv.x, progress);

    vec4 oColor = texture(iChannel0, uv);
    float a = col.r / oColor.r;
    fragColor = mix(oColor, vec4(col, 0.0), a);
}

/*    
   [ A new version is available that greatly improves performance! ] 

         ==>   https://www.shadertoy.com/view/43t3WX   <==


   It can be hard manipulate the definition and drawing of strings in shaders, it often
   relies on arrays of numbers that are tidious to update manually and are pretty 
   obfuscated compared to the original text.

   This shader tries to provide an easier framework for string manipulation where you
   can simply declare a string like this:

       makeStr(printHello) _H _e _l _l _o  __  _w _o _r _l _d    _end

   And directly use it in any function (More info in the "Common" tab):

       finalCol += printHello(uv);    
*/

// String declarations
makeStr(printStr1)      _E _a _s _y __ _w _a _y __ _t _o __ _p _r _i _n _t __ _t _e _x _t __  _i _n __ _S _h _a _d _e _r _s _EXC _end
makeStr(printNum)       _P _r _i _n _t __ _S _t _a _t _i _c __ _D _i _g _i _t _s __ _2 _0 _2 _3                                  _end
makeStr1i(printDynNum)  _A _n _d __ _D _y _n _a _m _i _c __ _D _i _g _i _t _s __ _dig(i)                                         _end
makeStr1f(printDecNum)  _D _e _c _i _m _a _l __ _N _u _m _b _e _r _s __ _T _o _o __ _dec(i, 3)                                   _end
makeStr2f(printDynChar) _S _u _p _p _o _r _t _s __ _D _y _n _a _m _i _c __ _C _h _a _r _a _c _t _e _r _s __ _ch(i) __ _ch(j)     _end
makeStr(printSpecial)   _A _n _d __ _e _v _e _n __ _S _p _e _c _i _a _l __ _C _h _a _r _s __ _EXC _NUM _MUL _DIV _AT _UND        _end

makeStr(printLong)      _W _o _r _k _s __ _f _o _r __ _a _l _l __ _t _e _x _t __ _s _i _z _e _s 
                        _COM __ _e _v _e _n __ _t _h _e __ _l _o _n _g _e _r __ _o _n _e _s __  
                        _i _f __ _y _o _u __ _s _c _a _l _e __ _t _h _e __ _u _v _s _EXC                                         _end

// Color declarations
#define RED     vec3( 1,.3,.4)
#define GREEN   vec3(.2, 1,.4)
#define BLUE    vec3(.2,.8, 1)
#define RAINBOW abs(cos(uv.x + vec3(5,6,1)))

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized uv coordinates
    vec2 uv = fragCoord / iResolution.y;

    // Final color
    vec3 col = vec3(0);

    // Font Size (higher values = smaller font)
    const float font_size = 9.;

    uv *= font_size;        // Scale font with font_size
    uv.y -= font_size - 1.; // Start drawing from the top


    col += RED * printStr1(uv);                       // "Easy way to print text in shaders"    
    uv.y += 2.; // Move the cursor down

    col += GREEN * printNum(uv);                      // "Static Digits"
    uv.y++;

    col += GREEN/.6 * printDynNum(uv, int(iTime)%10); // "Dynamic Digits"
    uv.y++; 

    col += GREEN/.4 * printDecNum(uv, iTime);         // "Decimal Numbers"
    uv.y+=2.;

    col += BLUE * printDynChar(uv, mod(iTime, 26.), mod(iTime*2., 26.)); // "Dynamic Characters"
    uv.y++;

    col += RAINBOW  * printSpecial(uv);                // "Special Characters"
    uv.y+=.7;

    uv *= 2.; // Multiply uv by 2 to make it smaller

    col += printLong(uv);                              // "Works for all text sizes..."

    fragColor = vec4(col, 1.);
}

// "Wonky Watch" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// Pocket watch with inexact timekeeping (up to +/- 10m) - only correct
// twice a day, just like Lewis Carroll's stopped clock, or when mouse pressed.

#define AA  1   // optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float PrTorusBxDf (vec3 p, vec3 b, float ri);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float ShowInt (vec2 q, vec2 cBox, float mxChar, float val);
float Fbm2 (vec2 p);

vec4 dateCur;
vec3 ltDir, qHit;
vec2 aaCs[3], wRotCs;
float dstFar, tCur, tShift;
int nFrame, idObj;
const int idFace = 1, idHandS = 2, idHand = 3, idAx = 4, idHub = 5,
   idCase = 6, idWnd = 7, idClsp = 8;
const float pi = 3.1415927;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, r, a;
  dMin = dstFar;
  p.xz = Rot2Cs (p.xz, wRotCs);
  p.yz = p.zy * vec2 (-1., 1.);
  q = p;
  r = length (q.xz);
  a = (r > 0.) ? atan (- q.z, q.x) / (2. * pi) + 0.5 : 0.;
  d = 0.9 * max (length (vec2 (r - 1.05, q.y)) - 0.08 - 0.003 * abs (sin (256. * pi * a)), 1.01 - r);
  DMINQ (idCase);
  d = PrCylDf (q.xzy, 1.05, 0.05);
  d = max (d, - max (length (vec2 (q.xz - vec2 (0., -0.5))) - 0.252, 0.03 - q.y));
  d = min (d, PrTorusBxDf ((q - vec3 (0.7, 0.05, 0.)).xzy, vec3 (0.06, 0.06, 0.01), 0.007));
  DMINQ (idFace);
  q = p;
  q.z -= 1.16;
  d = PrCylDf (q, 0.03, 0.06);
  DMINQ (idClsp);
  q.z -= 0.14;
  d = PrSphDf (q, 0.1);
  DMINQ (idWnd);
  d = PrTorusDf (q.xzy, 0.015, 0.13);
  DMINQ (idClsp);
  q = p;
  q.y -= 0.06;
  d = PrCylDf (q.xzy, 0.02, 0.04);
  DMINQ (idAx);
  q = p;
  q.yz -= vec2 (0.04, -0.5);
  d = PrCylDf (q.xzy, 0.015, 0.02);
  DMINQ (idAx);
  q = p;
  q.yz -= vec2 (0.045, -0.5);
  d = PrCylDf (q.xzy, 0.03, 0.008);
  DMINQ (idHub);
  q.xz = Rot2Cs (q.xz, aaCs[0]);
  q.z -= -0.1;
  d = PrRoundBoxDf (q, vec3 (0.008, 0., 0.1), 0.005);
  DMINQ (idHandS);
  q = p;
  q.y -= 0.07;
  d = PrCylDf (q.xzy, 0.04, 0.007);
  DMINQ (idHub);
  q.xz = Rot2Cs (q.xz, aaCs[1]);
  q.z -= -0.4;
  d = PrRoundBoxDf (q, vec3 (0.015, 0., 0.4), 0.005);
  DMINQ (idHand);
  q = p;
  q.y -= 0.09;
  d = PrCylDf (q.xzy, 0.04, 0.007);
  DMINQ (idHub);
  q.xz = Rot2Cs (q.xz, aaCs[2]);
  q.z -= -0.3;
  d = PrRoundBoxDf (q, vec3 (0.01, 0., 0.3), 0.005);
  DMINQ (idHand);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  float d;
  p.xz = Rot2Cs (p.xz, wRotCs);
  p.yz = p.zy * vec2 (-1., 1.);
  q = p;
  d = max (PrSphDf (q - vec3 (0., -7.35, 0.), 7.5), max (length (q.xz) - 1.05, - q.y));
  return d;
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 BgCol (vec3 rd)
{
  vec3 col, skCol;
  vec2 u;
  float el, f;
  skCol = vec3 (0.1, 0.15, 0.6);
  el = asin (rd.y);
  rd.xz = Rot2D (rd.xz, 0.01 * tCur);
  u = vec2 (atan (rd.z, - rd.x) + pi, tan (2. * atan (0.5 * el))) / (2. * pi);
  f = 64.;
  col = mix (skCol, vec3 (0.8, 0.8, 0.7), mix (Fbm2 (f * u),
     Fbm2 (f * (u - vec2 (1., 0.))), u.x));
  col = mix (col, skCol, smoothstep (0.95, 0.98, abs (el) / (0.5 * pi)));
  return col;
}

float LabSym (vec2 p)
{  // (from "Booze Cruise")
  vec2 q;
  float d, r;
  r = length (p);
  d = max (min (0.06 - abs (0.1 - abs (r - 0.8)), p.y), min (0.06 - abs (p.y), 1.1 - abs (p.x)));
  q = Rot2D (p, 2. * pi * floor (16. * ((r > 0.) ? atan (p.y, - p.x) / (2. * pi) : 0.) + 0.5) / 16.);
  d = max (d, min (min (0.06 - abs (q.y), 0.2 - abs (q.x + 1.1)), p.y + 0.1));
  q.x += 1.5;
  d = max (d, min (0.1 - length (q), p.y + 0.1));
  return d;
}

vec3 DialCol (vec2 p)
{
  vec3 col, c;
  vec2 q;
  float a, f, s;
  col = vec3 (1.);
  c = vec3 (0.3);
  q = p;
  s = length (q);
  a = atan (q.x, q.y) / pi;
  if (abs (fract (6. * a + 0.5) - 0.5) < 0.03 && abs (s - 0.95) < 0.05 ||
     abs (fract (30. * a + 0.5) - 0.5) < 0.1 && abs (s - 0.97) < 0.03 ||
     abs (6. * a) < 0.03 && abs (s - 0.92) < 0.08) col = c;
  col = mix (col, c, SmoothBump (0.98, 1., 0.005, s));
  q = (p - vec2 (0., -0.5)) / 0.25;
  s = length (q);
  a = atan (q.x, q.y) / pi;
  if (abs (fract (6. * a + 0.5) - 0.5) < 0.03 && abs (s - 0.93) < 0.07 ||
     abs (6. * a) < 0.03 && abs (s - 0.85) < 0.15) col = c;
  col = mix (col, c, SmoothBump (0.96, 1., 0.01, s));
  if (Maxv2 (abs (p - vec2 (0.7, 0.))) < 0.065) {
    col = vec3 (0.5);
    if (ShowInt (p - vec2 (0.755, -0.045), 0.3 * vec2 (0.4, 0.3), 2., dateCur.z) != 0.)
       col = vec3 (0.7, 1., 1.);
  }
  s = LabSym (8. * (p - vec2 (0., 0.4)));
  col = mix (mix (col, vec3 (0., 0., 0.8), smoothstep (-0.05, -0.01, s)),
     vec3 (1., 1., 0.2), smoothstep (0.01, 0.05, s));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo;
  float dstObj, dstTrObj, gRot, sh, rFac, nDotL;
  wRotCs = CosSin (0.2 * pi * sin (0.1 * pi * tCur));
  gRot = (dateCur.w + tShift) * 720. / (12. * 3600.);
  aaCs[0] = CosSin (2. * pi * (floor (60. * gRot) + smoothstep (0.7, 1., fract (60. * gRot))) / 60. - pi);
  gRot /= 60.;
  aaCs[1] = CosSin (2. * pi * gRot - pi);
  gRot /= 12.;
  aaCs[2] = CosSin (2. * pi * gRot - pi);
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    rFac = 0.;
    if (idObj == idFace) {
      if (qHit.y > 0.) {
        col4 = vec4 (DialCol (qHit.xz), 0.);
      } else {
        col4 = vec4 (0.7, 0.6, 0.1, 0.2);
        col4.rgb *= 1. - 0.3 * smoothstep (-0.02, 0., LabSym (8. * qHit.xz));
        rFac = 0.2;
      }
    } else if (idObj == idHandS) {
      col4 = (tShift == 0.) ? vec4 (0., 1., 0., 0.2) : vec4 (1., 0., 0., 0.2);
    } else if (idObj == idHand) {
      col4 = (abs (qHit.x) < 0.007 && qHit.y > 0.) ? vec4 (0.3, 1., 0.3, 0.2) :
         vec4 (0.3, 0.3, 0.3, 0.);
    } else if (idObj == idAx) {
      col4 = vec4 (0.6, 0.6, 0.5, 0.2);
    } else if (idObj == idHub) {
      col4 = vec4 (0.3, 0.3, 0.3, 0.);
    } else if (idObj == idCase) {
      col4 = vec4 (0.7, 0.6, 0.1, 0.2);
      rFac = 0.2;
    } else if (idObj == idWnd) {
