//      Coffin Illusion 1b       https://www.shadertoy.com/view/dltXzn
//
// ---------------------------------------------------------------------------------------

void mainImage(out vec4 O, vec2 u)
{
    u = trunc(1e2 * u / iResolution.y) / 1e2;

    vec2 c = trunc(u * 4. - .14) / 4. + .14, // center of nearest circle
         d = u - c;                          // delta to nearest circle     

    O = texelFetch(iChannel0,ivec2(32,0),0).x != 0. && abs(dot(d, d) - .01) < .001
        ? vec4(1, 0, 0, 1)
        : fract(4e4*sin((
            dot(d, d) < .01
                ? d.yx * vec2(1, -1) + c
                : u).y + vec4(1,7,4,0)));
}

