//      Coffin Illusion 1b       https://www.shadertoy.com/view/dltXzn
//
// ---------------------------------------------------------------------------------------

void mainImage(out vec4 O, vec2 u)
{
    u = trunc(1e2 * u / iResolution.y) / 1e2;

    vec2 c = trunc(u * 4. - .14) / 4. + .14,
         d = u - c;

    O = fract(4e4*sin((dot(d, d) < .01 ? d.yx * vec2(1, -1) + c : u).y + vec4(1,7,4,0)));
}

