#define numOfParts 150.
#define aTime iTime/64.

float hash21(vec2 p){ //pseudorandom generator, see The Art of Code on youtu.be/rvDo9LvfoVE
    p = fract(p*vec2(13.81, 741.76));
    p += dot(p, p+42.23);
    return fract(p.x*p.y);
}

vec2 parametricParabola(float t, vec2 seed){
    float d = .1 + 3.*hash21(.678*seed.yx); //y-stretch
    float c = sign(t) * (.01 + 2.*hash21(.987*seed)); //maximum shift
    float b = abs(c) + hash21(.285*seed) + .001; //x-stretch
    float a = c*c/b/b; //origin height
    t -= c/b;
    return vec2( b*t+c, (a-t*t)*d);
}

float lightUp(float dist, vec2 modif){ //light around dist=0
    return 6.*smoothstep(.025*modif.x, .0, dist)+clamp(.00008/dist/dist,.0,1.)+(1.+modif.y)*.0001/dist; //combined semi-hard shape with semi-soft & soft glow
}

vec2 originShift(){ //coordinate shift to create shapes
    vec2 s;
    float bTime = iTime/8.;

    if (fract(aTime)<.25)
        s = (fract(bTime)<.5)? //L
            vec2( .5, 2.*fract(2.*bTime)-1.):
            vec2( fract(-2.*bTime)-.5, 1);

    else if (fract(aTime)<.5)
        s = vec2(sin(2.*PI*bTime),cos(2.*PI*bTime)); //circle

    else if (fract(aTime)<.75)
        s = (fract(bTime)<.5)? //v
            vec2(.5, 1) * (2.*fract(2.*bTime)-1.) - vec2(.5,0):
            vec2(.5,-1) * (2.*fract(2.*bTime)-1.) + vec2(.5,0);

    else
        s = (fract(bTime)<.4)? //E
            vec2( .5, 2.*fract(bTime)/.4-1.):
            vec2( fract(-bTime/.2)-.5, -floor((fract(bTime)-.6)/.2));

    return s;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){

    // View definition
    vec2 uv = 3. * (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y); //long edge -3 to 3, square aspect ratio       
    uv += originShift(); //shape definitions

    // Draw particles
    float mySpeed, 
          myTime;
    vec2 myMod,
         seed = vec2(iTime);
    vec3 myColor,
         col = vec3(0);   

    for (float n=0.;n++<numOfParts;){
        myTime = iTime/2. + n/numOfParts;
        seed = vec2(ceil(myTime)*.123,ceil(myTime)*.456);
        mySpeed = sign(.5-hash21(seed+.123*n)) * (1.5+2.5*hash21(seed*n*.456));
        myMod = vec2( hash21(seed/n*.123), 5.+25.*hash21(seed/n*.456));
        myColor = fract(-myTime) * vec3( 1.+fract(myTime), .5+.6*fract(-myTime), .2+fract(-myTime)*fract(-myTime));
        col += myColor * lightUp( length( uv - parametricParabola( .7*fract(myTime)*mySpeed, seed*n)), myMod);
	} 

    // Add buffer
    vec3 amp = (fract(aTime*4.)<.99)? //zoom animation
        getDat( iChannel0, fragCoord).rgb: //read buffer
        getDat( iChannel0, fragCoord*1.04+iResolution.xy*(1.-1.04)/2.).rgb; //read scaled buffer
    col += 1.5/numOfParts *  amp*amp*amp; //overlay on image

    // Utility
    //col += step(fract(uv.x)-.005,.01)+step(fract(uv.y)-.005,.01); //grid

    fragColor = vec4(col,1.);
}

