    vec2 uv = vec2(fragCoord.xy / iResolution.xy) * bufferADownScale;
    float progressUV = (progressAVal-uv.y);
    float clampedProgress = max(0.,progressUV);
    clampedProgress = curveInterplation(clampedProgress);
    vec2 halfpixel = pixelOffset / (iResolution.xy / (2.0 + (1.-clampedProgress) * 1.));
