#define TAU 6.28318530718

// Magnification Amount
#define MAG 3.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Sample UV (offset for centering the spiral later)
    vec2 uv = fragCoord / iResolution.xy - 0.5;

    // Initial zoom-in
    uv *= pow(MAG, -1.0);

    // Create spiral multiplication
    uv *= ((atan(uv.x, uv.y) / TAU) + 0.5) * (MAG - 1.0) + 1.0;

    // Fetch the chroma key color
    vec4 chromaCol = texture(iChannel0, vec2(0.5, 0.5));

    // Loop for recursion
    for (int i=0; i<10; i++) {
        vec4 col = texture(iChannel0, uv + 0.5);

        // If similar color
        if (length(col - chromaCol) < 0.125) {

            // Zoom out UV
            vec2 newUv = uv * MAG;

            // If in frame, repeat loop
            if ((abs(newUv.x) < 0.5) && (abs(newUv.y) < 0.5)) {
                uv = uv * MAG;
                continue;
            }

        }
        break;

    }


    // Return the final color
    fragColor = texture(iChannel0, uv + 0.5);
}

#define res iResolution.xy
#define ss(a) smoothstep(-1./res.y, 1./res.y, a)

// Rough explanation + neater equations:
// https://www.desmos.com/calculator/obwtqgefsh
float bounce(float a, float n, float x) {   
    float s = sqrt(a);
    // Remove "(1.-s) *" to make all bounces occur in [0,1] domain
    x = 1. - min(1. - pow(s, n), (1.-s) * x) ;  
    float p = pow(s, floor(log2(x) / log2(s)));
    float m = (p - x) / (1.-s); 
    return 4. * m * (p - m);
}

void mainImage( out vec4 O, in vec2 px )
{
    vec2 uv = (px - 0. * res) / res.y;
    uv *= 2.;

    // Change me!
    float a = 0.5;  // Percentage of previous bounce's height, a in [0,1]
    float n = 6.;   // Number of bounces (any value >= 0)
    float r = 0.04; // Ball radius

    float t = mod(iTime, 3.8);
    float y = 0.5 * bounce(a, n, t);

    //r += 0.15 * y;
    vec2 p  = vec2(t, y + r);
    float d = length(uv - p);
    float s = ss(r - d);

    float yw = 0.5 * bounce(a, n, uv.x);
    float sw = ss(yw - uv.y + r);

    vec3 col = vec3(s, s, sw);

    O = vec4(col, 1);
}

