// ref: https://developer.nvidia.com/gpugems/gpugems2/part-iii-high-quality-rendering/chapter-20-fast-third-order-texture-filtering
// code & discussion:   https://stackoverflow.com/questions/13501081/efficient-bicubic-filtering-code-in-glsl/42179924#42179924
// ( thanks Matho for pointing ! :-) )

#define CH   iChannel0                       // try me ! :-)
#define R    vec2(textureSize(CH,0))         // texture size
#define T(d) texture(CH, (I+d) / R )         // fetch pixel I+offset d

vec4 cubic(float v){                         // prepare cubic coefficients for B-spline
 vec3  n = vec3(1,2,3) - v;
 return n*n*n / 6. * mat4x3(1,0,0, -4,1,0, 6,-4,1, -3,3,-1) + vec4(0,0,0,1);
}
// Catmull-Rom coefficients would allows true interpolation passing through values, but this would requires negative bilinear coefficients...

vec4 biCubic(vec2 U )                        //  the magic is here
{
    vec2 F = fract(U), I = floor(U);
    vec4 X = cubic(F.x),
         Y = cubic(F.y),
         Z = vec4(X.yw, Y.yw),
         s = vec4(X.xz, Y.xz) + Z,
         d = vec4(-1,1,-1,1) +.5 + Z / s;

    s.xz /= s.xz + s.yw;

    return mix( mix( T(d.yw), T(d.xw), s.x),
                mix( T(d.yz), T(d.xz), s.x), 
                s.z);
}

vec4 pseudoCubic(vec2 U )
{
    vec2 F = fract(U), I = floor(U);
    F *= F*(3.-F-F);                          // = smoothstep
    vec4 d = vec4(.5,-.5,.5,-.5) + 1.;
    return mix( mix( T(d.yw), T(d.xw), F.x),  // = texelFetch
                mix( T(d.yz), T(d.xz), F.x), 
                F.y);

}

void mainImage( out vec4 O, vec2 u )
{
    vec2 r = iResolution.xy,
         U = u/8.; // + iTime;
    O =  u.x < r.x*.6
           ? u.y > r.y*.4 
                     ? biCubic( U )                          // true biCubic
                     : texture(CH, U/R )                     // bilinear = T(I+F)
           : u.y > r.y*.4 
                     ? pseudoCubic( U )                      // smoothstep between pixels = T(I+smoothstep(F))
                     : texture(CH, (floor(U)+.5)/R );        // nearest  = T(I+.5) = texelFetch(iChannel0, ivec2(mod(U,R)), 0 );

 // O = .5+.5*sin(31.4*O);                 
    O = sqrt(O.rrrr);
}

