void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Shifted to [-1.0, 1.0] and zoom
    uv = (uv - 0.5) * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    // Julia Set Parameters
    vec2 c = vec2(0.5*sin(iTime), 0.5*cos(iTime));
    vec2 z = uv;

    float iter = 0.0;
    float max_iter = 100.0;

    // Mandelbrot Set Iterations
    while (iter < max_iter) {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (2.0 * z.x * z.y) + c.y;

        z = vec2(x,y);

        if (length(z) > 2.0) break;
        iter++;
    }

    // Color
    vec3 color = vec3(0.0);
    if (iter < max_iter) {
        color = 0.5 + 0.5*cos(3.0 + iter*0.15 + vec3(0.0,0.6,1.0));
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}

// The main function of a fragment shader, which sets the color of each pixel.
void mainImage( out vec4 color, vec2 UVcoords )
{
   // Calculate the center of the screen in both x and y directions
   float d3 = iResolution.y*.5 ,
         d4 =  iResolution.x*.5 ,

   // Use the time and mouse position to modulate a value (d2), which will be used to distort the UV coordinates
   // and to define a sine-wave period.
         d2 = 8.0 - 4.0 * sin(iTime*.07 )*1.,

   // Define a constant value (d1), which will be used as a sine-wave period
         d1 = .5,

   // Define a zoom factor
         zoomFactor = iMouse.y*0.2 - 10.;  

   // Shift UVcoords to the center of the screen and scale them by zoom factor
   UVcoords = .5*(UVcoords - vec2(d4,d3)) / zoomFactor;  

   // Define a macro function S. It adds a sin-wave pattern to the color.
#define S(X, Y, period)   color += vec4(sin(length(UVcoords + vec2(X,Y)*d2)*period));

   // Initialize color with alpha 1
    color = vec4(0.0, 0.0, 0.0, 1.0); 

    // Apply the sin-wave pattern to color using macro function S with different parameters.
    S(0.0,0.0,((iMouse.x*0.1)*.01))
    S(0,1.0*d2,d1)  
    S(0,-1.0*d2,d1)  
    S(-1.0*d2,-0.0,d1)  
    S(1.0*d2,0.0,d1)
}

/*
SUMMARY:

This shader creates a dynamically changing pattern based on UV coordinates and user input. 

Firstly, it calculates the center of the screen and adjusts the UV coordinates accordingly, also applying a zoom factor to these coordinates.

The primary color modulation is performed using the S macro, which uses a sinusoidal wave to generate the color pattern. This wave's period is influenced by time (creating an animation), mouse position (creating interactivity), and the adjusted UV coordinates (creating spatial variance).

In essence, this code creates an animated, zoomable, and interactive sin-wave color pattern that the user can manipulate through the mouse's position.
*/

vec2 polarCoords(vec2 coord) {
    float angle = atan(coord.y, coord.x);
    float radius = length(coord);
    return vec2(angle, radius);
}

vec3 calc( in vec2 p )
{
    float x = 0.5;
	float h = 0.0;
	for( int i=0; i<150; i++ )
	{
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));
		x = p.x*x*(1.0-x); h += log2(abs(p.x*(1.0-2.0*x)));

        x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
		x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
		x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
		x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
		x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
		x = p.y*x*(1.0-x); h += log2(abs(p.y*(1.0-2.0*x)));
	}
    h /= 150.0*12.0;


	vec3 col = vec3(0.0);
	if( h<0.0 )
	{
		h = abs(h);
		col = 0.5 + 0.5*sin( vec3(0.0,0.4,0.7) + 2.5*h );
		col *= pow(h,0.25);
	}


	return col;
}


float kaleidoscope(float angle, float numSegments) {
    return mod(numSegments * angle / (2.0 * 3.1415926) + 90.5, 3.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 coord = (iTime / cos(iTime * .0005)) / (uv - iTime /20.);

    vec2 polar = polarCoords(cos(coord));
    float angle = polar.x;
    float radius = polar.y;

    float jim = tan(abs(polar.x) * tan(polar.y));

    float result = kaleidoscope(sin(angle), 4.);


    vec3 color = mix(vec3(1.0, 4.5, 32.0), vec3(90.0, 100.5, 1.0), fract(result));

    fragColor = vec4(cos(color) * (iTime * .0009) * abs(jim) * cos(iTime * jim), 1.0);
}

